(109860, 'sensitizing', '<C><F><I><N><Q>@sensitize /''sensitaiz/ (sensitise) /''sensitaiz/<br />*  ngoại động từ<br />- sự làm cho dễ cảm động; khiến cho dễ cảm xúc, làm cho nhạy cảm<br />- tăng nhạy (phim ảnh, giấy ảnh)</Q></N></I></F></C>'),
(109861, 'sensitometer', '<C><F><I><N><Q>@sensitometer /,sensi''tɔmitə/<br />*  danh từ<br />- máy đo độ nhạy</Q></N></I></F></C>'),
(109862, 'sensitometers', '<C><F><I><N><Q>@sensitometer /,sensi''tɔmitə/<br />*  danh từ<br />- máy đo độ nhạy</Q></N></I></F></C>'),
(109863, 'sensitometry', '<C><F><I><N><Q>@sensitometry /,sensi''tɔmitri/<br />*  danh từ<br />- phép đo độ nhạy</Q></N></I></F></C>'),
(109864, 'sensor', '<C><F><I><N><Q>@sensor /''sensə/<br />*  danh từ<br />- (kỹ thuật),  (vật lý) phần tử nhạy</Q></N></I></F></C>'),
(109865, 'sensoria', '<C><F><I><N><Q>@sensoria /sen''sɔ:riəl/<br />*  danh từ,  số nhiều sensoria <br />/sensoria/<br />- bộ máy cảm giác; não tuỷ, chất xám vỏ não</Q></N></I></F></C><C><F><I><N><Q>@sensorium /sen''sɔ:riəl/<br />*  danh từ,  số nhiều sensoria <br />/sensoria/<br />- bộ máy cảm giác; não tuỷ, chất xám vỏ não</Q></N></I></F></C>'),
(109866, 'sensorial', '<C><F><I><N><Q>@sensorial /''sensəri/ (sensorial) /sen''sɔ:riəm/<br />*  tính từ<br />- (thuộc) bộ máy cảm giác; (thuộc) cảm giác; (thuộc) giác quan</Q></N></I></F></C>'),
(109867, 'sensorimoror', '<C><F><I><N><Q>@sensorimoror<br />* tính từ<br />- cảm nhận và vận động</Q></N></I></F></C>'),
(109868, 'sensorineural', '<C><F><I><N><Q>@sensorineural<br />* tính từ<br />- thuộc thần kinh cảm nhận</Q></N></I></F></C>'),
