INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(118115, 'stick', '<C><F><I><N><Q>@stick /stick/<br />*  danh từ<br />- cái gậy<br />- que củi<br />- cán (ô, gươm, chổi...)<br />- thỏi (kẹo, xi, xà phong cạo râu...); dùi (trống...)<br />- (âm nhạc) que chỉ huy nhạc (của người chỉ huy dàn nhạc)<br />- (hàng hải), (đùa cợt) cột buồm<br />- (nghĩa bóng) người đần độn; người cứng đờ đờ<br />- đợt bom<br />- (the sticks) (từ Mỹ,nghĩa Mỹ),  (thông tục) miền quê<br />!as cross as two sticks<br />- (xem) cross<br />!in a cleft stick<br />- ở vào thế bí, ở vào thế tiến lui đều khó<br />!to cut one''s stick<br />- (xem) cut<br />!to want the stich<br />- muốn phải đòn<br />*  ngoại động từ stuck<br />- đâm, thọc, chọc<br />=to stick a pin through something+ đâm đinh ghim qua một vật gì<br />=to stick pigs+ chọc tiết lợn<br />=cake stuck over with almonds+ bánh có điểm hạnh nhân<br />- cắm, cài, đặt, để, đội<br />=to stick pen behind one''s ear+ cài bút sau mái tai<br />=to stick up a target+ dựng bia lên<br />=stick your cap on+ đội mũ lên<br />- dán, dính<br />=to stick postage stamp on an envelope+ dán tem lên một cái phong bì<br />=stick no bills+ cấm dán quảng cáo<br />- cắm (cọc) để đỡ cho cây<br />- ((thường) + out) ngó, ló; ưỡn (ngựa), chìa (cằm), phình (bụng)...<br />=to stick one''s head out of window+ ló đầu ra ngoài cửa sổ<br />=to stick out one''s chest+ ưỡn ngực; vỗ ngực ta đây<br />- (ngành in) xếp (chữ)<br />- (từ Mỹ,nghĩa Mỹ) làm sa lầy, làm vướng mắc, làm trở ngại; giữ lại ((thường) động tính từ quá khứ)<br />=the wheels were stuck+ bánh xe bị sa lầy<br />=I was stuck in town+ tôi bị giữ lại ở tỉnh<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) làm bối rối, làm cuống, làm lúng túng<br />=I''m stuck by this question+ tôi bị câu hỏi đó<br />=to stick somebody up+ làm ai luống cuống<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) lừa bịp<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) chịu đựng<br />*  nội động từ<br />- cắm<br />=arrow sticks in target+ mũi tên cắm vào bia<br />- dựng đứng, đứng thẳng<br />=his hair stuck straight up+ tóc nó dựng đứng lên<br />- dính<br />=this envelope will not stick+ cái phong bì này không dính<br />- ((thường) + out) ló ra, chìa ra, ưỡn ra, nhô ra<br />- bám vào, gắn bó với, trung thành với<br />=to stick to the point+ bám vào vấn đề<br />=to stick like a bur+ bám như đỉa đói<br />=friends should stick together+ bạn bè phải gắn bó với nhau<br />=to stick to one''s promise+ trung thành với lời hứa của mình<br />=are you going to stick in all day?+ anh sẽ ở nhà suốt ngày à?<br />- sa lầy, mắc, kẹt, vướng, tắc; bị chặn lại, bị hoãn lại, đi đến chỗ bế tắc<br />=carriage stuck in the mud+ xe bị sa lầy<br />=he got through some ten lines and there stuck+ nói đọc trôi được mười dòng rồi tắc tị<br />=the bill stuck in committee+ đạo dự luật bị ngăn lại không được thông qua ở uỷ ban<br />- (từ Mỹ,nghĩa Mỹ) bối rối, luống cuống, lúng túng<br />!to stick around<br />- (từ lóng) ở quanh quẩn gần; lảng vảng gần<br />!to stick at<br />- miệt mài làm, bền bỉ tiếp tục<br />=to stick at a task for six hours+ miệt mài làm một công việc luôn sáu giờ liền<br />=to stick at nothing+ không gì ngăn trở được; không từ thủ đoạn nào<br />!to stick by<br />- trung thành với<br />!to stick down<br />- dán, dán lên, dán lại<br />- ghi vào (sổ...)<br />!to stick it [out]<br />- chịu đựng đến cùng<br />!to stick out for<br />- đòi; đạt được (cái gì)<br />!to stick to it<br />- khiên trì, bám vào (cái gì)<br />!to stick up<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) ăn cướp bằng vũ khí<br />!to stick up for<br />- (thông tục) bênh, bảo vệ (một người vắng mặt, quyền lợi...)<br />!to stick up to<br />- không khúm núm, không phục tùng; đương đầu lại<br />!to stick fast<br />- bị sa lầy một cách tuyệt vọng<br />!to stick in one''s gizzard<br />- (xem) gizzard<br />!to stick in the mud<br />- (xem) mud<br />!if you throw mud enough, some of it will stick<br />- nói xấu một trăm câu, thế nào cũng có người tin một câu<br />!some of the money stuck in (to) his fingers<br />- hắn tham ô một ít tiền<br /><br />@stick<br />- que; tay đòn; gậy<br />- control s. tay điều khiển</Q></N></I></F></C>'),
(118116, 'stick-in-the-mud', '<C><F><I><N><Q>@stick-in-the-mud /''stikinðəmʌd/<br />*  tính từ<br />- bảo thủ; chậm tiến<br />*  danh từ<br />- người bảo thủ; người chậm tiến, người lạc hậu<br />- (từ lóng) ông ấy, bà ấy, ông gì, bà gì</Q></N></I></F></C>'),
(118117, 'stick-ship', '<C><F><I><N><Q>@stick-ship<br />* danh từ<br />- sự quay trượt</Q></N></I></F></C>'),
(118118, 'stick-to-itiveness', '<C><F><I><N><Q>@stick-to-itiveness /,stik''tu:itivnis/<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) tính ương ngạnh, tính ương bướng</Q></N></I></F></C>'),
(118119, 'stick-to-liveness', '<C><F><I><N><Q>@stick-to-liveness<br />* danh từ<br />- (từ Mỹ, nghĩa Mỹ) tính ương ngạnh</Q></N></I></F></C>'),
(118120, 'stick-up', '<C><F><I><N><Q>@stick-up /''stikʌp/<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) sự ăn cướp</Q></N></I></F></C>'),
(118121, 'stick-up collar', '<C><F><I><N><Q>@stick-up collar /''stikʌp''kɔlə/<br />*  danh từ<br />- cổ cứng</Q></N></I></F></C>'),
(118122, 'sticker', '<C><F><I><N><Q>@sticker /''stikə/<br />*  danh từ<br />- người chọc; dao chọc (tiết lợn...)<br />- gai, ngạnh<br />- người dán<br />- nhãn có sẵn cồn dính<br />- người khách ngồi dai, người hay đến ám, người bám như đĩa ((từ Mỹ,nghĩa Mỹ) (cũng) stickler)<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) vấn đề khó giải quyết, vấn đề hắc búa</Q></N></I></F></C>'),
(118123, 'stickers', '<C><F><I><N><Q>@sticker /''stikə/<br />*  danh từ<br />- người chọc; dao chọc (tiết lợn...)<br />- gai, ngạnh<br />- người dán<br />- nhãn có sẵn cồn dính<br />- người khách ngồi dai, người hay đến ám, người bám như đĩa ((từ Mỹ,nghĩa Mỹ) (cũng) stickler)<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) vấn đề khó giải quyết, vấn đề hắc búa</Q></N></I></F></C>'),
(118124, 'stickful', '<C><F><I><N><Q>@stickful<br />* danh từ<br />- (in) tình trạng có nhiều chữ in trong một trang in</Q></N></I></F></C>'),
(118125, 'stickier', '<C><F><I><N><Q>@sticky /''stiki/<br />*  tính từ<br />- dính; sánh; bầy nhầy, nhớp nháp<br />- khó tính, khó khăn (tính nết)<br />=to be very sticky about something+ làm khó làm dễ cái gì 3 (từ lóng)<br />- hết sức khó chịu, rất đau đớn<br />=to come to a sticky end+ chết một cách rất đau đớn<br />- nóng và ẩm nồm (thời tiết)</Q></N></I></F></C>'),
(118126, 'stickiest', '<C><F><I><N><Q>@sticky /''stiki/<br />*  tính từ<br />- dính; sánh; bầy nhầy, nhớp nháp<br />- khó tính, khó khăn (tính nết)<br />=to be very sticky about something+ làm khó làm dễ cái gì 3 (từ lóng)<br />- hết sức khó chịu, rất đau đớn<br />=to come to a sticky end+ chết một cách rất đau đớn<br />- nóng và ẩm nồm (thời tiết)</Q></N></I></F></C>'),
(118127, 'stickily', '<C><F><I><N><Q>@stickily<br />* tính từ<br />- dính; nhớt; bầy nhầy, nhớp nháp<br />- &lt;thgt&gt; nồm (thời tiết nóng và ẩm một cách khó chịu, làm đổ mồ hôi)<br />- &lt;thgt&gt; khó tính, khó khăn (tính nết)<br />- &lt;thgt&gt; rất khó chịu, rất đau đớn<br />- &lt;thgt&gt; có phần phản đối</Q></N></I></F></C>'),
(118128, 'stickiness', '<C><F><I><N><Q>@stickiness /''stikinis/<br />*  danh từ<br />- tính chất dính; sự dính; tính bầy nhầy, tính nhớp nháp<br />- sự khó tính, tính khó khăn</Q></N></I></F></C>'),
(118129, 'sticking', '<C><F><I><N><Q>@sticking<br />* danh từ<br />- sự kẹt; móc; chêm<br />- sự dán; gắn<br />* tính từ<br />- dễ dính; dán; kẹt</Q></N></I></F></C><C><F><I><N><Q>@stick /stick/<br />*  danh từ<br />- cái gậy<br />- que củi<br />- cán (ô, gươm, chổi...)<br />- thỏi (kẹo, xi, xà phong cạo râu...); dùi (trống...)<br />- (âm nhạc) que chỉ huy nhạc (của người chỉ huy dàn nhạc)<br />- (hàng hải), (đùa cợt) cột buồm<br />- (nghĩa bóng) người đần độn; người cứng đờ đờ<br />- đợt bom<br />- (the sticks) (từ Mỹ,nghĩa Mỹ),  (thông tục) miền quê<br />!as cross as two sticks<br />- (xem) cross<br />!in a cleft stick<br />- ở vào thế bí, ở vào thế tiến lui đều khó<br />!to cut one''s stick<br />- (xem) cut<br />!to want the stich<br />- muốn phải đòn<br />*  ngoại động từ stuck<br />- đâm, thọc, chọc<br />=to stick a pin through something+ đâm đinh ghim qua một vật gì<br />=to stick pigs+ chọc tiết lợn<br />=cake stuck over with almonds+ bánh có điểm hạnh nhân<br />- cắm, cài, đặt, để, đội<br />=to stick pen behind one''s ear+ cài bút sau mái tai<br />=to stick up a target+ dựng bia lên<br />=stick your cap on+ đội mũ lên<br />- dán, dính<br />=to stick postage stamp on an envelope+ dán tem lên một cái phong bì<br />=stick no bills+ cấm dán quảng cáo<br />- cắm (cọc) để đỡ cho cây<br />- ((thường) + out) ngó, ló; ưỡn (ngựa), chìa (cằm), phình (bụng)...<br />=to stick one''s head out of window+ ló đầu ra ngoài cửa sổ<br />=to stick out one''s chest+ ưỡn ngực; vỗ ngực ta đây<br />- (ngành in) xếp (chữ)<br />- (từ Mỹ,nghĩa Mỹ) làm sa lầy, làm vướng mắc, làm trở ngại; giữ lại ((thường) động tính từ quá khứ)<br />=the wheels were stuck+ bánh xe bị sa lầy<br />=I was stuck in town+ tôi bị giữ lại ở tỉnh<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) làm bối rối, làm cuống, làm lúng túng<br />=I''m stuck by this question+ tôi bị câu hỏi đó<br />=to stick somebody up+ làm ai luống cuống<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) lừa bịp<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) chịu đựng<br />*  nội động từ<br />- cắm<br />=arrow sticks in target+ mũi tên cắm vào bia<br />- dựng đứng, đứng thẳng<br />=his hair stuck straight up+ tóc nó dựng đứng lên<br />- dính<br />=this envelope will not stick+ cái phong bì này không dính<br />- ((thường) + out) ló ra, chìa ra, ưỡn ra, nhô ra<br />- bám vào, gắn bó với, trung thành với<br />=to stick to the point+ bám vào vấn đề<br />=to stick like a bur+ bám như đỉa đói<br />=friends should stick together+ bạn bè phải gắn bó với nhau<br />=to stick to one''s promise+ trung thành với lời hứa của mình<br />=are you going to stick in all day?+ anh sẽ ở nhà suốt ngày à?<br />- sa lầy, mắc, kẹt, vướng, tắc; bị chặn lại, bị hoãn lại, đi đến chỗ bế tắc<br />=carriage stuck in the mud+ xe bị sa lầy<br />=he got through some ten lines and there stuck+ nói đọc trôi được mười dòng rồi tắc tị<br />=the bill stuck in committee+ đạo dự luật bị ngăn lại không được thông qua ở uỷ ban<br />- (từ Mỹ,nghĩa Mỹ) bối rối, luống cuống, lúng túng<br />!to stick around<br />- (từ lóng) ở quanh quẩn gần; lảng vảng gần<br />!to stick at<br />- miệt mài làm, bền bỉ tiếp tục<br />=to stick at a task for six hours+ miệt mài làm một công việc luôn sáu giờ liền<br />=to stick at nothing+ không gì ngăn trở được; không từ thủ đoạn nào<br />!to stick by<br />- trung thành với<br />!to stick down<br />- dán, dán lên, dán lại<br />- ghi vào (sổ...)<br />!to stick it [out]<br />- chịu đựng đến cùng<br />!to stick out for<br />- đòi; đạt được (cái gì)<br />!to stick to it<br />- khiên trì, bám vào (cái gì)<br />!to stick up<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) ăn cướp bằng vũ khí<br />!to stick up for<br />- (thông tục) bênh, bảo vệ (một người vắng mặt, quyền lợi...)<br />!to stick up to<br />- không khúm núm, không phục tùng; đương đầu lại<br />!to stick fast<br />- bị sa lầy một cách tuyệt vọng<br />!to stick in one''s gizzard<br />- (xem) gizzard<br />!to stick in the mud<br />- (xem) mud<br />!if you throw mud enough, some of it will stick<br />- nói 