(119114, 'string vest', '<C><F><I><N><Q>@string vest<br />* danh từ<br />- áo lưới (áo lót làm bằng vải có những mắt lưới to)</Q></N></I></F></C>'),
(119115, 'stringed', '<C><F><I><N><Q>@stringed /''striɳd/<br />*  tính từ<br />- có dây (đàn)<br />=the stringed instruments+ đàn dây<br />- buộc bằng dây</Q></N></I></F></C>'),
(119116, 'stringed instrument', '<C><F><I><N><Q>@stringed instrument<br />* danh từ<br />- đàn dây</Q></N></I></F></C>'),
(119117, 'stringency', '<C><F><I><N><Q>@stringency /''striɳʤənsi/<br />*  danh từ<br />- tính chính xác, tính nghiêm ngặt, tính chặt chẽ<br />- (tài chính) sự khan hiếm (tiền); tình hình khó làm ăn</Q></N></I></F></C>'),
(119118, 'stringendo', '<C><F><I><N><Q>@stringendo /''strin''dʤendou/<br />*  phó từ<br />- (âm nhạc) nhanh dần</Q></N></I></F></C>'),
(119119, 'stringent', '<C><F><I><N><Q>@stringent /''stridʤənt/<br />*  tính từ<br />- chính xác, nghiêm ngặt, chặt chẽ (nội quy, luật pháp...)<br />- (tài chính) khan hiếm (tiền); khó làm ăn<br /><br />@stringent<br />- ngặt; chính xác</Q></N></I></F></C>'),
(119120, 'stringently', '<C><F><I><N><Q>@stringently<br />* phó từ<br />- nghiêm ngặt, nghiêm khắc, chặt chẽ (về nội quy, luật pháp...)<br />- khan hiếm (tiền); khó làm ăn, khó khăn vì không có đù tiền (về hoàn cảnh tài chánh)</Q></N></I></F></C>'),
(119121, 'stringentness', '<C><F><I><N><Q>@stringentness<br />* danh từ<br />- xem stringency</Q></N></I></F></C>'),
(119122, 'stringer', '<C><F><I><N><Q>@stringer /''striɳə/<br />*  danh từ<br />- người lên dây đàn<br />- xà ngang (nối liền các cột nhà); gióng ngang (đỡ khung)<br />- (như) string-board</Q></N></I></F></C>'),
(119123, 'stringers', '<C><F><I><N><Q>@stringer /''striɳə/<br />*  danh từ<br />- người lên dây đàn<br />- xà ngang (nối liền các cột nhà); gióng ngang (đỡ khung)<br />- (như) string-board</Q></N></I></F></C>'),
(119124, 'stringhalt', '<C><F><I><N><Q>@stringhalt<br />- Cách viết khác : springhalt</Q></N></I></F></C>'),
