(119311, 'stubborner', '<C><F><I><N><Q>@stubborn /''stʌbən/<br />*  tính từ<br />- bướng bỉnh, ương bướng, ngoan cố<br />=as stubborn as a mule+ cứng đầu cứng cổ, ương bướng<br />- ngoan cường<br />=stubborn struggle+ cuộc đấu tranh ngoan cường<br />- không lay chuyển được; không gò theo được<br />=facts are stubborn things+ sự thật là điều không thể gò theo với lý thuyết được<br />=stubborn soil+ đất khó cày</Q></N></I></F></C>'),
(119312, 'stubborness', '<C><F><I><N><Q>@stubborness<br />* danh từ<br />- tính bướng bỉnh, tính ương ngạnh, tính ngoan cố<br />- tính ngoan cường; tính cứng cỏi; tính kiên quyết không chịu nhượng bộ<br />- tính chất khó di chuyển, khó cất bỏ, khó chữa...</Q></N></I></F></C>'),
(119313, 'stubbornest', '<C><F><I><N><Q>@stubborn /''stʌbən/<br />*  tính từ<br />- bướng bỉnh, ương bướng, ngoan cố<br />=as stubborn as a mule+ cứng đầu cứng cổ, ương bướng<br />- ngoan cường<br />=stubborn struggle+ cuộc đấu tranh ngoan cường<br />- không lay chuyển được; không gò theo được<br />=facts are stubborn things+ sự thật là điều không thể gò theo với lý thuyết được<br />=stubborn soil+ đất khó cày</Q></N></I></F></C>'),
(119314, 'stubbornly', '<C><F><I><N><Q>@stubbornly<br />* phó từ<br />- bướng bỉnh, ương ngạnh, ngoan cố<br />- ngoan cường; cứng cỏi; kiên quyết không chịu nhượng bộ<br />- khó di chuyển, khó cất bỏ, khó chữa</Q></N></I></F></C>'),
(119315, 'stubbornness', '<C><F><I><N><Q>@stubbornness /''stʌbənis/<br />*  danh từ<br />- tính bướng bỉnh, tính ương bướng, tính ngoan cố<br />- tính ngoan cường</Q></N></I></F></C>'),
(119316, 'stubby', '<C><F><I><N><Q>@stubby /''stʌbi/<br />*  tính từ<br />- có nhiều gốc cây (đất)<br />- ngăn và dày cộm (lông...)<br />- lùn và chắc mập (người)</Q></N></I></F></C>'),
(119317, 'stubs', '<C><F><I><N><Q>@stub /stʌb/<br />*  danh từ<br />- gốc (cây)<br />- chân (răng)<br />- mẩu (bút chì, thuốc lá, đuôi chó)<br />- (từ Mỹ,nghĩa Mỹ) cuống (séc, hoá đơn...)<br />- cái nhú ra, vật nhú ra<br />=a mere stub of a horn+ sừng mới nhu ra được một tí<br />- (như) stub_nail<br />*  ngoại động từ<br />- đánh gốc, đánh hết gốc (ở một khoảnh đất)<br />- giụi tắt (mẩu thuốc lá) ((cũng) stub out)<br />- vấp (ngón chân)<br />=to stub one''s toe against something+ vấp ngón chân vào vật gì</Q></N></I></F></C>'),
(119318, 'stucco', '<C><F><I><N><Q>@stucco /''stʌkou/<br />*  danh từ,  số nhiều stuccoes<br />- vữa xtucô (trát tường, đắp hình nổi trên tường...)<br />*  ngoại động từ<br />- trát bằng vữa xtucô</Q></N></I></F></C>'),
(119319, 'stucco-work', '<C><F><I><N><Q>@stucco-work /''stʌkouwə:k/<br />*  danh từ<br />- tường trát vữa xtucô, hình đắp nổi bằng vữa xtucô</Q></N></I></F></C>'),
(119320, 'stuccoed', '<C><F><I><N><Q>@stucco /''stʌkou/<br />*  danh từ,  số nhiều stuccoes<br />- vữa xtucô (trát tường, đắp hình nổi trên tường...)<br />*  ngoại động từ<br />- trát bằng vữa xtucô</Q></N></I></F></C>'),
