(119325, 'stuck-up', '<C><F><I><N><Q>@stuck-up /''stʌk''ʌp/<br />*  tính từ<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) học đòi làm sang<br />- ngông nghênh, ngạo mạn; tự cao tự đại</Q></N></I></F></C>'),
(119326, 'stud', '<C><F><I><N><Q>@stud /stʌd/<br />*  danh từ<br />- lứa ngựa nuôi<br />- trại nuôi ngựa giống<br />- ngựa giống<br />!at (in) stud<br />- có thể nuôi để lấy giống (súc vật)<br />*  danh từ<br />- đinh đầu lớn (đóng lồi ra để trang trí)<br />- núm cửa, quả đấm cửa (làm theo kiểu trang trí)<br />- Rivê, đinh tán<br />- khuy rời (luồn qua lỗ khuyết ở ngực và cổ áo sơ mi)<br />- cột (để đóng ván làm vách)<br />*  ngoại động từ<br />- đóng đinh đầu lớn<br />- làm núm cửa (để trang hoàng)<br />*  động tính từ quá khứ<br />- rải khắp<br />=sea studded with islands+ biển rải rác đầy đảo<br />=sky studded with stars+ bầu trời lốm đốm đầy sao<br />- dựng cột (cho một toà nhà để đóng ván làm vách)</Q></N></I></F></C>'),
(119327, 'stud and mud', '<C><F><I><N><Q>@stud and mud /''stʌdənd''mʌd/<br />*  danh từ<br />- (như) wattle_and_daub</Q></N></I></F></C>'),
(119328, 'stud-book', '<C><F><I><N><Q>@stud-book /''stʌdbuk/<br />*  danh từ<br />- sổ ghi lý lịch ngựa</Q></N></I></F></C>'),
(119329, 'stud farm', '<C><F><I><N><Q>@stud farm /''stʌd''fɑ:m/<br />*  danh từ<br />- trại nuôi ngựa</Q></N></I></F></C>'),
(119330, 'stud-hole', '<C><F><I><N><Q>@stud-hole<br />* danh từ<br />- lỗ khuy; lỗ khuyết</Q></N></I></F></C>'),
