INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(119405, 'stung', '<C><F><I><N><Q>@stung /stiɳ/<br />*  danh từ<br />- ngòi, vòi (ong, muỗi); răng độc (rắn)<br />- (thực vật học) lông ngứa<br />- nốt đốt (ong, rắn); nọc (đen &amp; bóng)<br />- sự sâu sắc, sự sắc sảo; sự chua chát (câu nói)<br />- sự đau nhói, sự đau quặn; sự day dứt<br />=the sting of hunger+ sự đau quặn của cơn đói<br />=the stings of remorse+ sự day dứt của hối hận<br />*  ngoại động từ stung<br />- châm, chích, đốt<br />- làm đau nhói; làm cay<br />=pepper stings one''s tongue+ hạt tiêu làm cay lưỡi<br />=smoke stings the eyes+ khói làm cay mắt<br />- cắn rứt, day dứt<br />=his conscience stung him+ lương tâm day dứt ai ta<br />- (từ lóng) ((thường) dạng bị động) bán đắt cho ai, bóp ai; lừa bịp<br />=he got stung on that deal+ hắn bị bịp trong việc mua bán đó<br />*  nội động từ<br />- đau nhói, đau nhức (một bộ phận cơ thể)<br />=tooth stings+ răng đau nhức<br />- đốt, châm<br />=some bees do not sting+ có những thứ ong không tốt<br />!nothing stings like the truth<br />- nói thật mất lòng</Q></N></I></F></C><C><F><I><N><Q>@sting /stiɳ/<br />*  danh từ<br />- ngòi, vòi (ong, muỗi); răng độc (rắn)<br />- (thực vật học) lông ngứa<br />- nốt đốt (ong, rắn); nọc (đen &amp; bóng)<br />- sự sâu sắc, sự sắc sảo; sự chua chát (câu nói)<br />- sự đau nhói, sự đau quặn; sự day dứt<br />=the sting of hunger+ sự đau quặn của cơn đói<br />=the stings of remorse+ sự day dứt của hối hận<br />*  ngoại động từ stung<br />- châm, chích, đốt<br />- làm đau nhói; làm cay<br />=pepper stings one''s tongue+ hạt tiêu làm cay lưỡi<br />=smoke stings the eyes+ khói làm cay mắt<br />- cắn rứt, day dứt<br />=his conscience stung him+ lương tâm day dứt ai ta<br />- (từ lóng) ((thường) dạng bị động) bán đắt cho ai, bóp ai; lừa bịp<br />=he got stung on that deal+ hắn bị bịp trong việc mua bán đó<br />*  nội động từ<br />- đau nhói, đau nhức (một bộ phận cơ thể)<br />=tooth stings+ răng đau nhức<br />- đốt, châm<br />=some bees do not sting+ có những thứ ong không tốt<br />!nothing stings like the truth<br />- nói thật mất lòng</Q></N></I></F></C>'),
(119406, 'stunk', '<C><F><I><N><Q>@stunk /stiɳk/<br />*  danh từ<br />- mùi hôi thối<br />- (số nhiều) (từ lóng) hoá học; tự nhiên học<br />!to raise a stink<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) bới thối ra, làm toáng lên<br />*  nội động từ stank,  stunk; stunk<br />- bốc mùi thối, bay mùi thối, thối<br />- tởm, kinh tởm<br />- (từ lóng) tồi, (thuộc) loại kém<br />*  ngoại động từ<br />- ((thường) + up) làm thối um<br />- (từ lóng) nhận thấy, trông thấy, ngửu thấy<br />=he can stink it a kilometer off+ xa một không kilômet nó cũng nhận ra điều đó<br />!to stink out<br />- làm bốc hơi thối để đuổi (ai) ra; đuổi (ai) ra bằng hơi thối<br />!to stink of money<br />- (từ lóng) giàu sụ</Q></N></I></F></C><C><F><I><N><Q>@stink /stiɳk/<br />*  danh từ<br />- mùi hôi thối<br />- (số nhiều) (từ lóng) hoá học; tự nhiên học<br />!to raise a stink<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) bới thối ra, làm toáng lên<br />*  nội động từ stank,  stunk; stunk<br />- bốc mùi thối, bay mùi thối, thối<br />- tởm, kinh tởm<br />- (từ lóng) tồi, (thuộc) loại kém<br />*  ngoại động từ<br />- ((thường) + up) làm thối um<br />- (từ lóng) nhận thấy, trông thấy, ngửu thấy<br />=he can stink it a kilometer off+ xa một không kilômet nó cũng nhận ra điều đó<br />!to stink out<br />- làm bốc hơi thối để đuổi (ai) ra; đuổi (ai) ra bằng hơi thối<br />!to stink of money<br />- (từ lóng) giàu sụ</Q></N></I></F></C>'),
(119407, 'stunned', '<C><F><I><N><Q>@stun /stʌn/<br />*  ngoại động từ<br />- làm choáng váng, làm bất tỉnh nhân sự<br />- làm sửng sốt, làm kinh ngạc<br />- làm điếc tai<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ) sự choáng váng; tình trạng bất tỉnh nhân sự<br />- đòn choáng váng, cú làm bất tỉnh nhân sự</Q></N></I></F></C>'),
(119408, 'stunner', '<C><F><I><N><Q>@stunner /''stʌnə/<br />*  danh từ<br />- (từ lóng) người cừ khôi, người rất thú vị<br />- chuyện hay, chuyện lý thú</Q></N></I></F></C>'),
(119409, 'stunners', '<C><F><I><N><Q>@stunner /''stʌnə/<br />*  danh từ<br />- (từ lóng) người cừ khôi, người rất thú vị<br />- chuyện hay, chuyện lý thú</Q></N></I></F></C>'),
(119410, 'stunning', '<C><F><I><N><Q>@stunning /''stʌniɳ/<br />*  tính từ<br />- làm choáng váng, làm bất tỉnh nhân sự<br />=a stunning blow+ một đòn choáng váng<br />- (từ lóng) tuyệt, hay, cừ khôi</Q></N></I></F></C><C><F><I><N><Q>@stun /stʌn/<br />*  ngoại động từ<br />- làm choáng váng, làm bất tỉnh nhân sự<br />- làm sửng sốt, làm kinh ngạc<br />- làm điếc tai<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ) sự choáng váng; tình trạng bất tỉnh nhân sự<br />- đòn choáng váng, cú làm bất tỉnh nhân sự</Q></N></I></F></C>'),
(119411, 'stunningly', '<C><F><I><N><Q>@stunningly<br />* phó từ<br />- xem stunning</Q></N></I></F></C>'),
(119412, 'stuns', '<C><F><I><N><Q>@stun /stʌn/<br />*  ngoại động từ<br />- làm choáng váng, làm bất tỉnh nhân sự<br />- làm sửng sốt, làm kinh ngạc<br />- làm điếc tai<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ) sự choáng váng; tình trạng bất tỉnh nhân sự<br />- đòn choáng váng, cú làm bất tỉnh nhân sự</Q></N></I></F></C>'),
(119413, 'stuns''l', '<C><F><I><N><Q>@stuns''l /''stʌdiɳseil/ (stun-sail) /''stʌnsl/ (stuns''l) /''stʌnsl/<br />-sail) <br />/''stʌnsl/ (stuns''l) <br />/''stʌnsl/<br />*  danh từ<br />- (hàng hải) buồm phụ</Q></N></I></F></C>'),
(119414, 'stunsail', '<C><F><I><N><Q>@stunsail /''stʌdiɳseil/ (stun-sail) /''stʌnsl/ (stuns''l) /''stʌnsl/<br />-sail) <br />/''stʌnsl/ (stuns''l) <br />/''stʌnsl/<br />*  danh từ<br />- (hàng hải) buồm phụ</Q></N></I></F></C>'),
(119415, 'stunt', '<C><F><I><N><Q>@stunt /stʌnt/<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ) sự làm cằn cỗi, sự làm còi cọc<br />- người còi cọc, con vật còi cọc<br />*  ngoại động từ<br />- chặn lại không cho phát triển, làm cằn cỗi, làm còi cọc<br />*  danh từ<br />- (thông tục) sự cố gắng kỳ công, sự cố gắng tập trung<br />- cuộc biểu diễn phô trương<br />- trò quảng cáo<br />=stunt article+ vật loè loẹt để quảng cáo<br />*  nội động từ<br />- (thông tục) biểu diễn nhào lộn<br />*  ngoại động từ<br />- biểu diễn nhào lộn bằng (máy bay...)</Q></N></I></F></C>'),
(119416, 'stunt man', '<C><F><I><N><Q>@stunt man /''stʌnt''mæn/<br />*  danh từ<br />- (điện ảnh) người đóng thế (cho vai chính) những cảnh nguy hiểm</Q></N></I></F></C>'),
