INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(120447, 'suing', '<C><F><I><N><Q>@sue /sju:/<br />*  ngoại động từ<br />- kiện<br />=to sue to somebody for damages+ kiện ai đòi bồi thường<br />*  nội động từ<br />- đi kiện, kiện<br />- yêu cầu, thỉnh cầu, khẩn khoản<br />=to sue to someone for something+ yêu cầu người nào một việc gì<br />=to sue for a woman''s hand+ cầu hôn<br />!to sue out<br />- (pháp lý) xin và được ân xá</Q></N></I></F></C>'),
(120448, 'suint', '<C><F><I><N><Q>@suint<br />* danh từ<br />- mỡ lông cừu<br />- mồ hôi dầu</Q></N></I></F></C>'),
(120449, 'suit', '<C><F><I><N><Q>@suit /sju:t/<br />*  danh từ<br />- bộ com lê, bộ quần áo (đàn ông)<br />=dress suit+ bộ quần áo dạ hội<br />- lời xin, lời yêu cầu, lời thỉnh cầu<br />=to make suit+ xin xỏ<br />=to prosper in one''s suit+ đạt lời yêu cầu<br />- sự cầu hôn<br />- sự kiện tụng, sự tố tụng<br />- (đánh bài) Hoa<br />- bộ quần áo giáp<br />- (hàng hải) bộ buồm<br />*  ngoại động từ<br />- làm cho phù hợp<br />- (động tính từ quá khứ) thích hợp; quen; đủ điều kiện<br />=he is not suited to be a teacher+ anh ta không có đủ điều kiện làm một giáo viên<br />- thoả mãn, đáp ứng nhu cầu của, phù hợp với quyền lợi của<br />=it does not suit all tastes+ điều đó không thoả mãn tất cả thị hiếu<br />- hợp với, thích hợp với<br />=this climate does not suit him+ khí hậu ở đây không hợp với anh ta<br />=the part suits him admirably+ vai đó hợp với anh ta quá<br />*  nội động từ<br />- tiện, hợp với<br />=that date will suit+ ngày ấy tiện<br />=red does not suit with her complexion+ màu đỏ không hợp với nước da của cô ta<br />!suit yourself<br />- tuỳ anh muốn làm gì thì làm</Q></N></I></F></C>'),
(120450, 'suitability', '<C><F><I><N><Q>@suitability /,sju:tə''biliti/ (suitableness) /''sju:təblnis/<br />*  danh từ<br />- sự hợp, sự thích hợp</Q></N></I></F></C>'),
