INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(121247, 'sure', '<C><F><I><N><Q>@sure /ʃuə/<br />*  tính từ<br />- chắc, chắc chắn<br />=you may be sure of his honesty+ anh có thể chắc chắn rằng nó chân thật<br />=I''m sure I didn''t mean to hurt your feelings+ tôi thề là không có ý xúc phạm đến anh<br />- chắc chắn, có thể tin cậy được; cẩn thận<br />=to send something by a sure hand+ nhờ một người cẩn thận chuyển hộ cái gì<br />=to put something in a sure place+ để cái gì vào nơi chắc chắn<br />=a sure shot+ tay súng rất chắc, nhà thiện xạ<br />- thật, xác thật<br />=to be sure she is not pretty+ thật ra mà nói, cô ta không xinh<br />=so it is, to be sure!+ đấy, thật là như thế!<br />!to make sure<br />- chắc chắn<br />- nắm chắc, làm cho chắc chắn<br />!show and sure<br />- chậm mà chắc<br />*  phó từ<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) chắc chắn<br />=it sure was cold+ chắc chắn là rét<br />- (từ cổ,nghĩa cổ) tôi thừa nhận<br />=''tis pleasant, sure, to see one''s name in print+ tôi thừa nhận là thấy tên mình trên báo thì ai cũng thích<br />!as sure as eggs is eggs<br />!as sure as a gun<br />- chắc như đinh đóng cột, chắc như hai với hai là bốn<br />!for sure<br />- chắc chắn<br />!sure enough<br />- (thông tục) chắc chắn, không còn nghi ngờ gì nữa<br /><br />@sure<br />- chắc chắn</Q></N></I></F></C>'),
(121248, 'sure-enough', '<C><F><I><N><Q>@sure-enough /''ʃuəi''nʌf/<br />*  tính từ<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) đúng, thực, chắc</Q></N></I></F></C>'),
(121249, 'sure-fire', '<C><F><I><N><Q>@sure-fire /''ʃuə,faiə/<br />*  tính từ<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) chắc chắn thành công, chắc chắn như mong đợi, không thể chệch được, không thể thất bại</Q></N></I></F></C>'),
(121250, 'sure-footed', '<C><F><I><N><Q>@sure-footed /''ʃuə''futid/<br />*  tính từ<br />- chắc chân, không ngã được, không trượt được<br />- không nhầm được</Q></N></I></F></C>'),
