INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(125914, 'touches', '<C><F><I><N><Q>@touch /tʌtʃ/<br />*  danh từ<br />- sự sờ, sự mó, sự đụng, sự chạm<br />- xúc giác<br />- nét (vẽ); ngón (đàn); bút pháp, văn phong<br />=a bold touch+ một nét vẽ bạo<br />=to add a few finishing touches+ hoàn chỉnh, thêm vào một vài nét hoàn chỉnh<br />- một chút, một ít<br />=a touch of jealousy+ hơi ghen<br />=a touch of salt+ một chút muối<br />=a touch of indigestion+ hơi bị đầy bụng<br />- sự tiếp xúc, sự giao thiệp; quan hệ; sự dính líu, sự dính dáng<br />=to keep in touch with+ giữ quan hệ với; có dính líu với<br />=to be out of touch with+ không có quan hệ với, mất liên lạc với<br />=to lose touch with+ mất liên lạc với, tiếp xúc đến<br />- (thể dục,thể thao) đường biên (bóng đá)<br />- (âm nhạc) lối bấm phím<br />- (y học) phép thăm bệnh bằng cách sờ<br />- (từ cổ,nghĩa cổ) sự thử thách, sự thử; đá thử<br />=to put to the touch+ đem thử thách<br />=true as touch+ rất chính xác<br />*  ngoại động từ<br />- sờ, mó, đụng, chạm<br />- đạt tới, đến<br />=I can touch the ceiling+ tôi có thể với tới trần<br />=the thermometer touched 37 o; yesterday+ hôm qua nhiệt biểu lên tới 37 o<br />=to touch bottom+ xuống dốc đến cùng cực<br />=to touch the spot+ (thông tục) gãi đúng chỗ ngứa<br />- gần, kề, sát bên, liền<br />=his garden touches ours+ vườn của anh ta sát liền ngay với vườn của chúng tôi<br />- đả động đến, nói đến, đề cập đến, nói chạm đến<br />=to touch [on] a subject in the conversation+ đả động đến một vấn đề trong khi nói chuyện<br />- gõ nhẹ, đánh nhẹ (chuông), gảy (đàn); bấm (phím đàn)<br />=to touch the piano+ bấm phím đàn pianô<br />- đụng vào, dính vào, mó vào, vầy vào, vọc vào<br />=please do not touch my papers+ xin đừng mó vào giấy tờ của tôi<br />- có liên quan, có quan hệ với, dính dáng, dính líu<br />=the question touched your interests+ vấn đề có dính líu đến quyền lợi của anh<br />=what happens to him doesn''t touch me at all+ những việc xảy đến với nó không có liên quan gì tới tôi cả<br />- đụng đến, ăn, uống, dùng đến<br />=he promises not to touch alcohol+ hắn hứa là không đụng đến một giọt rượu<br />- làm cảm động, làm mủi lòng, gợi mối thương tâm, làm xúc động<br />=his grief touched us deeply+ nỗi đau buồn của anh ta làm chúng tôi vô cùng xúc động<br />- làm mếch lòng, làm phật lòng, chạm lòng tự ái, xúc phạm<br />=that touched him home+ điều đó làm anh ta hết sức phật lòng; điều đó chạm lòng tự ái của anh ta<br />- có ảnh hưởng, có tác dụng<br />=nothing you can say will touch him+ tất cả những điều anh có thể nói sẽ chả có tác dụng gì đối với nó<br />- làm hư nhẹ, gây thiệt hại nhẹ, làm hỏng nhẹ<br />=the frost has touched the vines+ sương giá làm hư hại nhẹ những giàn nho<br />- sánh kịp, bằng, tày<br />=nobody can touch him in causticity+ không ai có thể sánh kịp ông ta về lối châm biếm chua cay<br />- (hàng hải) cặp, ghé (bến...)<br />=the ship touched Colombo+ con tàu cặp bến Cô-lom-bô<br />- (từ lóng) gõ, vay<br />=he touched me for 10 d+ nó gõ tôi lấy mười đồng, nó vay tôi mười đồng<br />*  nội động từ<br />- chạm nhau, đụng nhau<br />=do the báll touch?+ những quả bóng có chạm nhau không?<br />- gần sát, kề nhau<br />!to touch at<br />- (hàng hải) cặp, ghé, đỗ vào (bến...)<br />=to touch at a port+ cặp bến<br />!to touch down<br />- (thể dục,thể thao) chạm đường biên ngang<br />- (hàng không) hạ cánh<br />!to touch in<br />- vẽ phác, phác hoạ<br />=to touch in the eyebrows of the portrait+ vẽ phác đôi lông mày của bức chân dung<br />!to touch off<br />- cắt đứt (đường dây điện thoại, sự liên lạc bằng điện thoại)<br />- vẽ phác, phác hoạ<br />- bắn, nổ, xả, nhả (đạn)<br />- gây ra, phát động (phong trào phản đối...)<br />!to touch on (upon)<br />- bàn đến, đả động đến, nói đến, đề cập đến<br />=to touch on (upon) a subject+ đề cập đến một vấn đề<br />!to touch up<br />- tô, sửa qua<br />=to touch up a drawing+ sửa qua bức vẽ<br />- quất roi vào (ngựa...)<br />!to touch with<br />- nhuốm, đượm, ngụ<br />=praise touched with jealousy+ lời khen ngợi thoáng ngụ ý ghen tuông</Q></N></I></F></C>'),
(125915, 'touchier', '<C><F><I><N><Q>@touchy /''tʌtʃi/<br />*  tính từ<br />- hay giận dỗi, dễ động lòng</Q></N></I></F></C>'),
(125916, 'touchiest', '<C><F><I><N><Q>@touchy /''tʌtʃi/<br />*  tính từ<br />- hay giận dỗi, dễ động lòng</Q></N></I></F></C>'),
(125917, 'touchily', '<C><F><I><N><Q>@touchily<br />* phó từ<br />- hay giận dỗi, dễ bị đụng chạm, hay tự ái<br />- dễ động lòng, quá nhạy cảm<br />- đòi hỏi giải quyết thận trọng (một vấn đề, tình huống  vì có khả năng gây ra tranh cãi hoặc xúc phạm)</Q></N></I></F></C>'),
(125918, 'touchiness', '<C><F><I><N><Q>@touchiness /''tʌtʃinis/<br />*  danh từ<br />- tính hay giận dỗi, tính dễ động lòng</Q></N></I></F></C>'),
