(125921, 'touchstone', '<C><F><I><N><Q>@touchstone /''tʌtʃstoun/<br />*  danh từ<br />- đá thử vàng<br />- tiêu chuẩn</Q></N></I></F></C>'),
(125922, 'touchstones', '<C><F><I><N><Q>@touchstone /''tʌtʃstoun/<br />*  danh từ<br />- đá thử vàng<br />- tiêu chuẩn</Q></N></I></F></C>'),
(125923, 'touchwood', '<C><F><I><N><Q>@touchwood /''tʌtʃwud/<br />*  danh từ<br />- bùi nhùi (để nhóm lửa)</Q></N></I></F></C>'),
(125924, 'touchy', '<C><F><I><N><Q>@touchy /''tʌtʃi/<br />*  tính từ<br />- hay giận dỗi, dễ động lòng</Q></N></I></F></C>'),
(125925, 'tough', '<C><F><I><N><Q>@tough /tʌf/<br />*  tính từ<br />- dai, bền<br />=tough meat+ thịt dai<br />=tough rubber+ cao su dai<br />- dai sức, dẻo dai; mạnh mẽ (người)<br />=tough constitution+ thể chất mạnh mẽ dẻo dai<br />- cứng rắn, cứng cỏi, bất khuất<br />=a tough spirit+ một tinh thần bất khuất<br />- khăng khăng, cố chấp, ương ngạnh, ngoan cố<br />=a tough customer+ một gã khó trị, một gã ngoan cố<br />- khó, hắc búa, gay go (vấn đề, bài toán)<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) thằng du côn, tên vô lại; tên kẻ cướp</Q></N></I></F></C>'),
(125926, 'toughen', '<C><F><I><N><Q>@toughen /''tʌfn/<br />*  ngoại động từ<br />- làm dai, làm bền, làm cứng rắn, làm dẻo dai, làm quen chịu đựng (gian khổ...), tôi luyện<br />*  nội động từ<br />- rắn lại<br />- trở nên cứng rắn, trở nên dẻo dai, trở nên quen chịu đựng</Q></N></I></F></C>'),
(125927, 'toughened', '<C><F><I><N><Q>@toughen /''tʌfn/<br />*  ngoại động từ<br />- làm dai, làm bền, làm cứng rắn, làm dẻo dai, làm quen chịu đựng (gian khổ...), tôi luyện<br />*  nội động từ<br />- rắn lại<br />- trở nên cứng rắn, trở nên dẻo dai, trở nên quen chịu đựng</Q></N></I></F></C>'),
(125928, 'toughening', '<C><F><I><N><Q>@toughen /''tʌfn/<br />*  ngoại động từ<br />- làm dai, làm bền, làm cứng rắn, làm dẻo dai, làm quen chịu đựng (gian khổ...), tôi luyện<br />*  nội động từ<br />- rắn lại<br />- trở nên cứng rắn, trở nên dẻo dai, trở nên quen chịu đựng</Q></N></I></F></C>'),
(125929, 'toughens', '<C><F><I><N><Q>@toughen /''tʌfn/<br />*  ngoại động từ<br />- làm dai, làm bền, làm cứng rắn, làm dẻo dai, làm quen chịu đựng (gian khổ...), tôi luyện<br />*  nội động từ<br />- rắn lại<br />- trở nên cứng rắn, trở nên dẻo dai, trở nên quen chịu đựng</Q></N></I></F></C>'),
(125930, 'tougher', '<C><F><I><N><Q>@tough /tʌf/<br />*  tính từ<br />- dai, bền<br />=tough meat+ thịt dai<br />=tough rubber+ cao su dai<br />- dai sức, dẻo dai; mạnh mẽ (người)<br />=tough constitution+ thể chất mạnh mẽ dẻo dai<br />- cứng rắn, cứng cỏi, bất khuất<br />=a tough spirit+ một tinh thần bất khuất<br />- khăng khăng, cố chấp, ương ngạnh, ngoan cố<br />=a tough customer+ một gã khó trị, một gã ngoan cố<br />- khó, hắc búa, gay go (vấn đề, bài toán)<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) thằng du côn, tên vô lại; tên kẻ cướp</Q></N></I></F></C>'),
(125931, 'toughest', '<C><F><I><N><Q>@tough /tʌf/<br />*  tính từ<br />- dai, bền<br />=tough meat+ thịt dai<br />=tough rubber+ cao su dai<br />- dai sức, dẻo dai; mạnh mẽ (người)<br />=tough constitution+ thể chất mạnh mẽ dẻo dai<br />- cứng rắn, cứng cỏi, bất khuất<br />=a tough spirit+ một tinh thần bất khuất<br />- khăng khăng, cố chấp, ương ngạnh, ngoan cố<br />=a tough customer+ một gã khó trị, một gã ngoan cố<br />- khó, hắc búa, gay go (vấn đề, bài toán)<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) thằng du côn, tên vô lại; tên kẻ cướp</Q></N></I></F></C>'),
(125932, 'toughie', '<C><F><I><N><Q>@toughie<br />* danh từ<br />- &lt;thgt&gt; thằng du côn, tên vô lại; người thô bạo và hung tợn</Q></N></I></F></C>'),
(125933, 'toughish', '<C><F><I><N><Q>@toughish /''tʌfiʃ/<br />*  tính từ<br />- hơi dai, hơi bền<br />- khá cứng cỏi<br />- hơi cố chấp, hơi ương ngạnh, hơi ngoan cố<br />- hơi khó, hơi hắc búa; hơi vất vả khó nhọc (việc làm)</Q></N></I></F></C>'),
