INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(126807, 'traverse', '<C><F><I><N><Q>@traverse /''trævə:s/<br />*  danh từ<br />- sự đi ngang qua<br />- (toán học) đường ngang<br />- thanh ngang, xà ngang, đòn ngang<br />- (hàng hải) đường chữ chi (để tránh gió ngược...)<br />- đường tắt (leo núi)<br />=to make a traverse+ vượt núi bằng đường tắt<br />- (quân sự) sự xoay (súng) cho đúng hướng<br />- (quân sự) tường che chiến hào<br />- (pháp lý) sự chối<br />- (nghĩa bóng) sự phản đối, sự chống lại; điều làm trở ngại, điều cản trở<br />=it is a traverse for his plan+ đó là một điều trở ngại cho kế hoạch của hắn<br />*  ngoại động từ<br />- đi qua, đi ngang qua, vượt qua; nằm vắt ngang<br />=to traverse a forest+ đi qua một khu rừng<br />=the railway traverses the country+ đường sắt chạy ngang qua vùng này<br />- đi (một quãng đường), đi theo (một đường phố)<br />- nghiên cứu kỹ lưỡng, xét kỹ toàn bộ<br />=to traverse a subject+ nghiên cứu toàn bộ một vấn đề<br />- (pháp lý) chối (trong lời biện hộ)<br />- (quân sự) xoay (chĩa) (súng) đúng hướng<br />- (ngành đường sắt) chuyển tải, sang toa<br />- bào (gỗ) ngang thớ<br />- (nghĩa bóng) phản đối, chống lại, làm thất bại (kế hoạch...)<br />=to traverse an opinion+ phản đối một ý kiến<br />*  nội động từ<br />- xoay quanh trục (kim la bàn...)<br />- đi đường tắt (leo núi)<br />- (pháp lý) chối (trong khi biện hộ)<br />*  tính từ<br />- đặt ngang, vắt ngang<br />!traverse sailing<br />- (hàng hải) sự đi theo đường chữ chi (để tránh gió ngược...)</Q></N></I></F></C>'),
