INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(77904, 'modern', '<C><F><I><N><Q>@modern /''mɔdən/<br />*  tính từ<br />- hiện đại<br />=modern science+ khoa học hiện đại<br />- cận đại<br />=modern history+ lịch sử cận đại<br />*  danh từ<br />- người cận đại, người hiện đại<br />- người ưa thích cái mới, người có quan điểm mới<br /><br />@modern<br />- mới, hiện đại</Q></N></I></F></C>'),
(77905, 'Modern quantity theory of money', '<C><F><I><N><Q>@Modern quantity theory of money<br />- (Econ) Thuyết định lượng tiền tệ hiện đại.<br />+ Xem MONETARISM, MONEY, THE DEMAND FOR.</Q></N></I></F></C>'),
(77906, 'Modern sector', '<C><F><I><N><Q>@Modern sector<br />- (Econ) Khu vực hiện đại.<br />+ Một tên gọi khác của khu vực công nghiệp, hoặc đôi khi dùng để gọi khu vực chính phủ.</Q></N></I></F></C>'),
(77907, 'modernise', '<C><F><I><N><Q>@modernise /''mɔdə:naiz/ (modernise) /''mɔdə:naiz/<br />*  ngoại động từ<br />- hiện đại hoá; đổi mới<br />*  nội động từ<br />- thành hiện đại; thành mới</Q></N></I></F></C>'),
(77908, 'modernism', '<C><F><I><N><Q>@modernism /''mɔdə:nizm/<br />*  danh từ<br />- quan điểm mới, phương pháp mới; quan điểm hiện đại, phương pháp hiện đại<br />- (ngôn ngữ học) từ ngữ cận đại<br />- chủ nghĩa tân thời<br />- (tôn giáo) chủ nghĩa đổi mới</Q></N></I></F></C>'),
(77909, 'modernist', '<C><F><I><N><Q>@modernist /''mɔdə:nist/<br />*  danh từ<br />- người ủng hộ cái mới<br />- người theo chủ nghĩa tân thời<br />- (tôn giáo) người theo chủ nghĩa đổi mới</Q></N></I></F></C>'),
(77910, 'modernistic', '<C><F><I><N><Q>@modernistic /,mɔdə:''nistik/<br />*  tính từ<br />- (thuộc) quan điểm (tư tưởng, phương pháp...) hiện đại</Q></N></I></F></C>'),
(77911, 'modernists', '<C><F><I><N><Q>@modernist /''mɔdə:nist/<br />*  danh từ<br />- người ủng hộ cái mới<br />- người theo chủ nghĩa tân thời<br />- (tôn giáo) người theo chủ nghĩa đổi mới</Q></N></I></F></C>'),
(77912, 'modernity', '<C><F><I><N><Q>@modernity /mɔ''də:niti/<br />*  danh từ<br />- tính chất hiện đại<br />- cái hiện đại</Q></N></I></F></C>'),
