(91507, 'pitchman', '<C><F><I><N><Q>@pitchman /''pitʃmən/<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ) người bán quán ở vỉa hè</Q></N></I></F></C>'),
(91508, 'pitchmen', '<C><F><I><N><Q>@pitchman /''pitʃmən/<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ) người bán quán ở vỉa hè</Q></N></I></F></C>'),
(91509, 'pitchy', '<C><F><I><N><Q>@pitchy /''pitʃi/<br />*  tính từ<br />- (thuộc) hắc ín; giống hắc ín; đen như hắc ín</Q></N></I></F></C>'),
(91510, 'piteous', '<C><F><I><N><Q>@piteous /''pitiəs/<br />*  tính từ<br />- đáng thương hại, thảm thương</Q></N></I></F></C>'),
(91511, 'piteously', '<C><F><I><N><Q>@piteously<br />* phó từ<br />- thảm thương, đáng thương hại</Q></N></I></F></C>'),
(91512, 'piteousness', '<C><F><I><N><Q>@piteousness /''pitiəsnis/<br />*  danh từ<br />- tình trạng đáng thương, tình trạng thảm thương</Q></N></I></F></C>'),
(91513, 'pitfall', '<C><F><I><N><Q>@pitfall /''pitfɔ:l/<br />*  danh từ<br />- hầm bẫy<br />- (nghĩa bóng) cạm bẫy<br />=a road strewn with pitfalls+ con đường đầy chông gai cạm bẫy</Q></N></I></F></C>'),
(91514, 'pitfalls', '<C><F><I><N><Q>@pitfall /''pitfɔ:l/<br />*  danh từ<br />- hầm bẫy<br />- (nghĩa bóng) cạm bẫy<br />=a road strewn with pitfalls+ con đường đầy chông gai cạm bẫy</Q></N></I></F></C>'),
(91515, 'pith', '<C><F><I><N><Q>@pith /piθ/<br />*  danh từ<br />- ruột cây<br />- lớp vỏ xốp; cùi (quả cam)<br />- (giải phẫu) tuỷ sống<br />- (nghĩa bóng) phần chính, phần cốt tuỷ ((thường) the pith and marrow of)<br />=the pith and marrow of matter+ phần chính của vấn đề<br />- sức mạnh; nghị lực<br />=to lack pith+ thiếu nghị lực<br />*  ngoại động từ<br />- rút tuỷ sống để giết (một con vật)</Q></N></I></F></C>'),
(91516, 'pith hat', '<C><F><I><N><Q>@pith hat<br />* danh từ<br />- mũ bấc (làm bằng cây phơi khô đội để tránh nắng trước đây)</Q></N></I></F></C>'),
(91517, 'pith helmet', '<C><F><I><N><Q>@pith helmet<br />* danh từ<br />- mũ bấc (làm bằng cây phơi khô đội để tránh nắng trước đây)</Q></N></I></F></C>'),
(91518, 'pithecan', '<C><F><I><N><Q>@pithecan<br />* danh từ<br />- người vượn</Q></N></I></F></C>'),
(91519, 'pithecanthrope', '<C><F><I><N><Q>@pithecanthrope /,piθi''kænθroup/<br />*  danh từ<br />- người vượn</Q></N></I></F></C>'),
(91520, 'pithecoil', '<C><F><I><N><Q>@pithecoil /pi''θi:kɔid/<br />*  tính từ<br />- giống vượn, giống khỉ</Q></N></I></F></C>'),
(91521, 'pithed', '<C><F><I><N><Q>@pith /piθ/<br />*  danh từ<br />- ruột cây<br />- lớp vỏ xốp; cùi (quả cam)<br />- (giải phẫu) tuỷ sống<br />- (nghĩa bóng) phần chính, phần cốt tuỷ ((thường) the pith and marrow of)<br />=the pith and marrow of matter+ phần chính của vấn đề<br />- sức mạnh; nghị lực<br />=to lack pith+ thiếu nghị lực<br />*  ngoại động từ<br />- rút tuỷ sống để giết (một con vật)</Q></N></I></F></C>'),
(91522, 'pithier', '<C><F><I><N><Q>@pithy /''piθi/<br />*  tính từ<br />- (thuộc) ruột cây; giống ruột cây; nhiều ruột (cây)<br />- (thuộc) tuỷ sống; giống tuỷ sống, nhiều tuỷ sống<br />- mạnh mẽ, đấy sức sống, đầy nghị lực<br />- súc tích; rắn rỏi, mạnh mẽ (văn)</Q></N></I></F></C>'),
(91523, 'pithiest', '<C><F><I><N><Q>@pithy /''piθi/<br />*  tính từ<br />- (thuộc) ruột cây; giống ruột cây; nhiều ruột (cây)<br />- (thuộc) tuỷ sống; giống tuỷ sống, nhiều tuỷ sống<br />- mạnh mẽ, đấy sức sống, đầy nghị lực<br />- súc tích; rắn rỏi, mạnh mẽ (văn)</Q></N></I></F></C>'),
(91524, 'pithily', '<C><F><I><N><Q>@pithily /''piθili/<br />*  phó từ<br />- mạnh mẽ, đấy sức sống, đầy nghị lực<br />- súc tích; rắn rỏi, mạnh mẽ (văn)</Q></N></I></F></C>'),
(91525, 'pithiness', '<C><F><I><N><Q>@pithiness /''piθinis/<br />*  danh từ<br />- sức mạnh, sức sống<br />- tính súc tích; tính rắn rỏi, tính mạnh mẽ (văn)</Q></N></I></F></C>'),
(91526, 'pithing', '<C><F><I><N><Q>@pith /piθ/<br />*  danh từ<br />- ruột cây<br />- lớp vỏ xốp; cùi (quả cam)<br />- (giải phẫu) tuỷ sống<br />- (nghĩa bóng) phần chính, phần cốt tuỷ ((thường) the pith and marrow of)<br />=the pith and marrow of matter+ phần chính của vấn đề<br />- sức mạnh; nghị lực<br />=to lack pith+ thiếu nghị lực<br />*  ngoại động từ<br />- rút tuỷ sống để giết (một con vật)</Q></N></I></F></C>'),
(91527, 'pithless', '<C><F><I><N><Q>@pithless /''piθlis/<br />*  tính từ<br />- không có ruột (cây)<br />- không có tuỷ sống<br />- yếu đuối, bạc nhược, không có nghị lực</Q></N></I></F></C>'),
(91528, 'piths', '<C><F><I><N><Q>@pith /piθ/<br />*  danh từ<br />- ruột cây<br />- lớp vỏ xốp; cùi (quả cam)<br />- (giải phẫu) tuỷ sống<br />- (nghĩa bóng) phần chính, phần cốt tuỷ ((thường) the pith and marrow of)<br />=the pith and marrow of matter+ phần chính của vấn đề<br />- sức mạnh; nghị lực<br />=to lack pith+ thiếu nghị lực<br />*  ngoại động từ<br />- rút tuỷ sống để giết (một con vật)</Q></N></I></F></C>'),
(91529, 'pithy', '<C><F><I><N><Q>@pithy /''piθi/<br />*  tính từ<br />- (thuộc) ruột cây; giống ruột cây; nhiều ruột (cây)<br />- (thuộc) tuỷ sống; giống tuỷ sống, nhiều tuỷ sống<br />- mạnh mẽ, đấy sức sống, đầy nghị lực<br />- súc tích; rắn rỏi, mạnh mẽ (văn)</Q></N></I></F></C>'),
(91530, 'pitiable', '<C><F><I><N><Q>@pitiable /''pitiəbl/<br />*  tính từ<br />- đáng thương, đáng thương hại, đáng thương xót<br />- đáng khinh</Q></N></I></F></C>'),
(91531, 'pitiableness', '<C><F><I><N><Q>@pitiableness /''pitiəblnis/<br />*  danh từ<br />- tình trạng đáng thương, tình trạng đáng thương hại, tình trạng đáng thương xót<br />- tình trạng đáng khinh</Q></N></I></F></C>'),
(91532, 'pitiably', '<C><F><I><N><Q>@pitiably<br />* phó từ<br />- đáng thương, gợi lên lòng thương xót<br />- đáng khinh</Q></N></I></F></C>'),
(91533, 'pitied', '<C><F><I><N><Q>@pity /''piti/<br />*  danh từ<br />- lòng thương hại, lòng thương xót, lòng trắc ẩn<br />=to take (have) pity on someone; to feel pity for someone+ thương xót ai, thương hại ai, động lòng trắc ẩn đối với ai<br />=for pity''s sake+ vì lòng thương hại<br />=out of pity+ vì lòng trắc ẩn<br />- điều đáng thương hại, điều đáng tiếc<br />=what a pity!+ thật đáng tiếc!<br />=the pity is that...+ điều đáng tiếc là...<br />=it''s a thousand pities that...+ rất đáng tiếc là...<br />*  ngoại động từ<br />- thương hại, thương xót, động lòng trắc ẩn đối với<br />=he is much to be pitied+ thằng cha thật đáng thương hại</Q></N></I></F></C>'),
(91534, 'pities', '<C><F><I><N><Q>@pity /''piti/<br />*  danh từ<br />- lòng thương hại, lòng thương xót, lòng trắc ẩn<br />=to take (have) pity on someone; to feel pity for someone+ thương xót ai, thương hại ai, động lòng trắc ẩn đối với ai<br />=for pity''s sake+ vì lòng thương hại<br />=out of pity+ vì lòng trắc ẩn<br />- điều đáng thương hại, điều đáng tiếc<br />=what a pity!+ thật đáng tiếc!<br />=the pity is that...+ điều đáng tiếc là...<br />=it''s a thousand pities that...+ rất đáng tiếc là...<br />*  ngoại động từ<br />- thương hại, thương xót, động lòng trắc ẩn đối với<br />=he is much to be pitied+ thằng cha thật đáng thương hại</Q></N></I></F></C>'),
(91535, 'pitiful', '<C><F><I><N><Q>@pitiful /''pitiful/<br />*  tính từ<br />- thương xót, thương hại, đầy lòng trắc ẩn<br />- đáng thương hại<br />- nhỏ mọn, không đáng kể (vật)<br />=a pitiful amount+ số lượng không đáng kể<br />- đáng khinh</Q></N></I></F></C>'),
(91536, 'pitifully', '<C><F><I><N><Q>@pitifully<br />* phó từ<br />- một cách đáng thương<br />- một cách tầm thường</Q></N></I></F></C>'),
(91537, 'pitifulness', '<C><F><I><N><Q>@pitifulness /''pitifulnis/<br />*  danh từ<br />- lòng thương hại, lòng thương xót, lòng trắc ẩn<br />- tình trạng đáng thương hại<br />- tình trạng đáng khinh</Q></N></I></F></C>'),
(91538, 'pitiless', '<C><F><I><N><Q>@pitiless /''pitilis/<br />*  tính từ<br />- tàn nhẫn, nhẫn tâm, không thương xót</Q></N></I></F></C>'),
(91539, 'pitilessly', '<C><F><I><N><Q>@pitilessly<br />* phó từ<br />- tàn bạo, nhẫn tâm, không thương xót<br />- gây gắt, khắc nghiệt; không ngớt</Q></N></I></F></C>'),
