INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(91653, 'places', '<C><F><I><N><Q>@place /pleis/<br />*  danh từ<br />- nơi, chỗ, địa điểm, địa phương (thành phố, tỉnh, làng...)<br />=in all places+ ở khắp nơi<br />=a native of the place+ một người sinh trưởng ở địa phương ấy<br />- nhà, nơi ở, nơi (dùng vào mục đích gì)<br />=to have a nice little place in the country+ có một ngôi nhà nhỏ xinh xinh ở nông thôn<br />=can''t you come to my place?+ anh có thể đến chơi đằng nhà tôi được không?<br />=places of amusement+ những nơi vui chơi<br />- chỗ, vị trí, địa vị; chỗ ngồi, chỗ đứng; chỗ thích đáng; chỗ thích hợp<br />=everything in its place+ vật nào chỗ ấy<br />=a sore place on the wrist+ chỗ đau ở cổ tay<br />=to give place to someone+ tránh chỗ cho ai đi, nhường chỗ cho ai<br />=to change places with somebody+ đổi chỗ cho ai<br />=if I were in your place+ nếu tôi ở địa vị anh<br />=this is no place for children+ đây không phải chỗ cho trẻ con<br />=the remark is out of place+ lời nhận xét không đúng chỗ (không đúng lúc, không thích hợp)<br />- chỗ làm<br />=to get a place in...+ kiếm được một chỗ làm ở...<br />- nhiệm vụ, cương vị<br />=it is not my place to inquire into that+ tôi không có nhiệm vụ tì hiểu việc ấy<br />=to keep aomebody in his place+ bắt ai phải giữ đúng cương vị, không để cho ai vượt quá cương vị<br />- địa vị (xã hôi), cấp bậc, thứ bậc, hạng<br />=to ottain a high place+ đạt địa vị cao sang<br />=to get the first place in the race+ được xếp hạng nhất trong cuộc đua, về nhất trong cuộc đua<br />- đoạn sách, đoạn bài nói<br />=I''ve lost my place+ tôi không tìm được đoạn tôi đã đọc<br />- quảng trường, chỗ rộng ((thường) có tên riêng ở trước); đoạn phố<br />- (quân sự) vị trí<br />=the place can be defended+ có thể bảo vệ được vị trí đó<br />- (toán học) vị trí (của một con số trong một dãy số)<br />=calculated to five places of decimals+ được tính đến năm số lẻ<br />- thứ tự<br />=in the first place+ thứ nhất, trước hết<br />=in the second place+ thứ nhì, kế đó<br />=in the last place+ cuối cùng<br />!in place of<br />- thay vì, thay cho, thay thế vào<br />!to look out of place<br />- có vẻ lúng túng<br />!not quite in place<br />- không đúng chỗ, không thích hợp<br />=the proposal is not quite in place+ đề nghị ấy không thích hợp<br />!to take place<br />- xảy ra, được cử hành được tổ chức<br />*  ngoại động từ<br />- để, đặt<br />=to place everything in good order+ để mọi thứ có thứ tự<br />=the house is well placed+ ngôi nhà được đặt ở một vị trí rất tốt<br />=to place confidence in a leader+ đặt tin tưởng ở một lãnh tụ<br />- cứ làm, đưa vào làm, đặt vào (cương vị công tác)<br />=to be placed in command of the regiment+ được cử làm chỉ huy trung đoàn ấy<br />- đầu tư (vốn)<br />- đưa cho, giao cho<br />=to place an order for goods with a firm+ (đưa đơn) đặt mua hàng của một công ty<br />=to place a book with a publisher+ giao cho một nhà xuất bản in và phát hành một cuốn sách<br />=to place a matter in someone''s hands+ giao một vấn đề cho ai giải quyết<br />=to place a child under someone''s care+ giao một đứa bé cho ai chăm nom hộ<br />- xếp hạng<br />=to be placed third+ được xếp hạng ba<br />- bán<br />=a commodity difficult to place+ một mặt hàng khó bán<br />- nhớ (tên, nơi gặp gỡ...)<br />=I know his face but I can''t place him+ tôi biết mặt nó nhưng không nhớ tên nó tên gì và gặp ở đâu<br />- đánh giá (ai, về tính tình, địa vị...)<br />=he is a difficult man to place+ khó đánh giá được anh ấy<br />- (thể dục,thể thao) ghi (bàn thắng bóng đá) bằng cú đặt bóng sút<br /><br />@place<br />- chỗ, vị trí; hàng // đặt để<br />- decimal p. hàng chữ số thập phân <br />- tens'' p. ở hàng chục<br />- thousands'' p. ở hàng nghìn</Q></N></I></F></C>'),
(91654, 'placet', '<C><F><I><N><Q>@placet /''pleiset/<br />*  danh từ<br />- sự biểu quyết "đông y" <br />*  thán từ<br />- đồng ý! (trong khi biểu quyết)<br />=non placet!+ không đồng ý!</Q></N></I></F></C>'),
