INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(99970, 'ramps', '<C><F><I><N><Q>@ramp /ræmp/<br />*  danh từ<br />- dốc, bờ dốc, đoạn đường thoai thoải<br />=approach ramp of a bridge+ dốc lên cầu<br />- (hàng không) thang lên máy bay<br />- bệ tên lửa<br />*  nội động từ<br />- dốc thoai thoải<br />- chồm đứng lên, chồm hai chân trước lên (sư tử trên huy hiệu)<br />-(đùa cợt) nổi xung, giận điên lên<br />*  ngoại động từ<br />- (kiến trúc) xây dốc thoai thoải (một bức tường)<br />*  danh từ<br />- (từ lóng) sự lừa đảo<br />- sự tăng giá cao quá cao<br />*  nội động từ<br />- lừa đảo<br />- tăng giá quá cao</Q></N></I></F></C>'),
(99971, 'ramrod', '<C><F><I><N><Q>@ramrod /''ræmrɔd/<br />*  danh từ<br />- que nhồi thuốc (súng hoả mai)<br />- cái thông nòng (súng)</Q></N></I></F></C>'),
(99972, 'ramrods', '<C><F><I><N><Q>@ramrod /''ræmrɔd/<br />*  danh từ<br />- que nhồi thuốc (súng hoả mai)<br />- cái thông nòng (súng)</Q></N></I></F></C>'),
(99973, 'rams', '<C><F><I><N><Q>@ram /ræm/<br />*  danh từ<br />- cừu đực (chưa thiến)<br />- (hàng hải) mũi nhọn (của tàu chiến để đâm thủng hông tàu địch); tàu chiến có mũi nhọn<br />- (kỹ thuật) đấm nện; búa đóng cọc, búa đập, búa thuỷ động<br />- sức nện của búa đóng cọc<br />- pittông (của bơm đẩy)<br />- (kỹ thuật) máy đẩy cốc (luyện kim)<br />- (the ram) (thiên văn học) chòm sao Bạch dương<br />*  ngoại động từ<br />- nện (đất...)<br />- đóng cọc<br />- nạp (súng) đầy đạn<br />- nhét vào, nhồi vào, ấn chặt vào<br />=to ram one''s clothes into a bag+ nhét quần áo vào một cái túi<br />- (hàng hải) đâm thủng (một tàu khác) bằng mũi nhọn<br />- đụng, đâm vào<br />=to ram one''s head against the wall+ đụng đầu vào tường<br />!to ram an argument home<br />- câi lý đến cùng; cố gắng thuyết phục<br />!to ram something down someone''s throat<br />- (xem) throat</Q></N></I></F></C>'),
(99974, 'Ramsey pricing', '<C><F><I><N><Q>@Ramsey pricing<br />- (Econ) Định giá Ramsay.<br />+ Đã được sử dụng rộng rãi trong thyết ngoại ứng môi trường, một qui tắc có thể cần thiết áp dụng cho mội mức giá trong một nền kinh tế, khi ít nhất có một thứ hàng hoá là hàng công cộng không thể cạn kiệt được.</Q></N></I></F></C>'),
(99975, 'ramshackle', '<C><F><I><N><Q>@ramshackle /''ræm,ʃækl/<br />*  tính từ<br />- xiêu vẹo, đổ nát<br />=a ramshackle house+ ngôi nhà xiêu vẹo đổ nát</Q></N></I></F></C>'),
(99976, 'ramule', '<C><F><I><N><Q>@ramule<br />* danh từ<br />- (thực vật học) cành nhỏ; nhánh</Q></N></I></F></C>'),
(99977, 'ramus', '<C><F><I><N><Q>@ramus<br />* danh từ<br />- số nhiều rami<br />- (thần kin) nhánh; sợi (lông chim); sợi nhánh</Q></N></I></F></C>'),
(99978, 'ran', '<C><F><I><N><Q>@ran /rʌn/<br />* xem run</Q></N></I></F></C><C><F><I><N><Q>@run /rʌn/<br />*  danh từ<br />- sự chạy<br />=at a run+ đang chạy<br />=on the run all day+ chạy ngược, chạy xuôi suốt ngày, bạn rộn suốt ngày<br />=to be on the run+ chạy đi, chạy trốn, chuồn<br />=to break into a run+ bắt đầu chạy<br />=to keep the enemy on the run+ truy kích (đuổi theo) quân địch<br />=to go for a short run before breakfast+ chạy một quâng ngắn trước khi ăn sáng<br />- cuộc hành trình ngắn, cuộc đi tham quan ngắn; cuộc đi dạo, cuộc đi chơi<br />=a run up to town+ cuộc đi thăm tỉnh ngắn ngày<br />- chuyến đi; quâng đường đi (xe lửa, tàu thuỷ...)<br />=it is only a 30 minute''s run to our place+ đến chỗ chúng tôi ở chỉ mất 30 phút<br />- sự hoạt động, sự vận hành, sự chạy (máy móc...); thời gian vận hành<br />- sự giảm nhanh, sự tụt nhanh, sự hạ nhanh, sự sụp đổ nhanh<br />=the temperature came down with a run+ độ nhiệt giảm nhanh<br />=run of ground+ sự lở đất, sự sụp đất<br />- thời gian liên tục, hồi, cơn, loạt<br />=a long run of power+ sự nắm quyền trong một thời gian dài<br />=a run of luck+ hồi đó<br />=the play has a run of 50 nights+ vở kịch được diễn đi diễn lại trong một thời gian liên tục năm mươi đêm liền<br />- tầng lớp đại đa số, loại bình thường; hạng bình thường<br />=the commom run of men; the run of mankind+ những người bình thường<br />=the run of the mill+ những sản phẩm bình thường của nhà máy<br />- loại, hạng, thứ (hàng hoá)<br />- đàn (cá...), bầy (súc vật...)<br />- sân nuôi (gà, cịt...), cánh đồng cỏ (nuôi cừu...), bâi rào kín (để chăn nuôi)<br />- dấu vết quâng đường thường lui tới (của một thú rừng...)<br />- máng dẫn nước<br />- (từ Mỹ,nghĩa Mỹ) ngòi, lạch, nước, dòng suối<br />- hướng; chiều hướng, xu thế<br />=the run of the mountains is N.E.+ dây núi chạy theo hướng đông bắc<br />=the run of public opinion+ chiều hướng của dư luận<br />- nhịp điệu (của một câu thơ...)<br />- dải liên tục, đường dây liên tục, dòng mạch chạy dài<br />=a run of gold+ mạch mỏ vàng chạy dài<br />=run of tide+ dòng thuỷ triều<br />- sự đổ xô tới; nhu cầu lớn, sự đòi hỏi nhiều (một thứ hàng gì...)<br />=a run on the bank+ sự đổ xô tới đòi rút tiền ở nhà ngân hàng ra<br />=the book has a considerable run+ quyển sách bán được rất chạy (được nhiều người hỏi mua)<br />- sự cho phép tự do sử dụng<br />=to have the run of somebody''s books+ được phép tự do sử dụng sách của ai<br />- (hàng không) sự bay theo đường thẳng với một tốc độ cố định (trước khi hoặc lúc ném bom)<br />- (ngành mỏ) mặt nghiêng, mặt dốc<br />- (hàng hải) phần đáy đuôi tàu (đáy tàu về phía bánh lái)<br />- (âm nhạc) Rulat<br />!in the long run<br />- (xem) long<br />!to keep the run of something<br />- (từ Mỹ,nghĩa Mỹ) nắm được diễn biến của việc gì, nắm được việc gì<br />!to lose the run of something<br />- không nắm được diễn biến của việc gì, không nắm được việc gì<br />!to make a run of it<br />- chạy trốn, trốn thoát<br />!out of the common run<br />- khác thường, không bình thường<br />=to put the run the somebody+ buộc ai phải chạy trốn<br />!to take (have) the run for one''s money<br />- được hưởng những sự vui thích xứng với đồng tiền bỏ ra; được vui thích bõ công khó nhọc<br />!with a run<br />!by the run<br />- rất nhanh, nhanh vùn vụt<br />- ngay lập tức, không chậm trễ<br />*  nội động từ ran,  run<br />- chạy<br />=to run dowen a slope+ chạy xuống con đường dốc<br />=a cold shiver ran down gis spine+ cơn rùng mình ớn lạnh chạy suốt dọc theo xương sống anh ta<br />- chạy vội, vội vã<br />=to run to meet somebody+ vội vã đến gặp ai<br />=to run to help somebody+ chạy vội đến giúp ai<br />- chạy trốn, tẩu thoát<br />=to run for one''s life+ chạy trốn bán sống bán chết<br />=to run for it+ (thông tục) chạy trốn<br />=to cut and run+ (từ lóng) chuồn, tẩu<br />- chạy đua<br />=to run in a race+ chạy đua<br />=to run second+ chạy về thứ nhì<br />- chạy, vận hành, hoạt động (máy móc, nhà máy...)<br />=to leave the engine of the motorcar running+ để cho động cơ ô tô chạy<br />- trôi đi, lướt đi, trượt đi, chạy lướt, lăn mau...<br />=the pen runs on the paper+ ngòi bút chạy lướt trên trang giấy<br />=time runs fast+ thời gian trôi nhanh<br />=how his tongue runs!+ mồm nó cứ nói lem lẻm suốt đi!<br />=his life runs smoothly+ cuộc đời anh ta cứ êm dềm trôi đi<br />=the rope runs freely in the pulley+ cái dây thừng trượt đi một cách dễ dàng trên ròng rọc<br />- xoay quanh (một cái trục...; một vấn đề...)<br />=that is the point on which the whole argument runs+ đó là điểm mà tất cả cuộc tranh luận xoay quanh<br />- bỏ khắp, mọc lan ra (cây)<br />- chạy dài, chạy quanh<br />=the road runs across a plain+ con đường chạy qua cánh đồng<br />=the moutain range runs North and South+ dây núi chạy dài từ phía bắc đến phía nam<br />=the fence runs round the house+ hàng rao bao quanh ngôi nhà<br />- được viết, được thảo, được kể, có nội dung (thư, văn kiện, câu chuyện...)<br />=the letter runs as follows+ bức thư được viết như sau<br />=the story runs in these words+ câu chuyện được kể như thế này<br />- tiếp tục tồn tại, tiếp diễn trong một quãng thời gian liên tục, kéo dài<br />=the play has been running for six months+ vở kịch được diễn đi diễn lại sáu tháng liền<br />- có giá trị, có hiệu lực<br />=the contract runs for seven years+ bản giao kèo có giá trị trong bảy năm<br />- ám ảnh, vương vấn<br />=the tune is still running in my head+ điệu nhạc đó cứ vương vấn mãi trong óc tôi<br />- lưu luyến, truyền mãi, còn mãi mãi<br />=it runs in the family+ cái đó truyền mâi trong gia đình<br />- lan nhanh, truyền đi<br />=the news ran like wild fire+ tin tức lan đi rất nhanh<br />- hướng về, nghĩ về<br />=the eyes run over something+ đưa mắt nhìn (hướng về) cái gì<br />=to run back over the past+ nghĩ về quá khứ<br />- chạy trên tuyến đường (xe khách, tàu chở khách...)<br />=the boat runs between Hanoi and Namdinh+ con tàu chạy trên tuyến đường Hà nội  Nam định<br />- nhoè (mực); thôi (màu), phai, bạc (màu)<br />- chảy<br />=blood runs in veins+ máu chảy trong mạch máu<br />=the tide runs strong+ thuỷ triều chảy mạnh<br />=pus is running+ mủ chảy<br />=nose runs+ mũi chảy nước<br />=eyes run+ chảy nước mắt<br />- đầm đìa, lênh láng, dầm dề<br />=to be running with sweat+ đầm đìa mồ hôi<br />=to be running with blood+ máu đổ lênh láng<br />- rỉ rò (chùng, chậu...)<br />- lên tới, đạt tới<br />=rice runs five tons a hectare this year+ năm nay lúa đạt năm tấn một hecta<br />- trở nên, trở thành, có xu thế, có chiều hướng<br />=potatoes run big this year+ khoai tây năm nay to củ<br />=to run mad+ hoá điên<br />=to run to extremes+ đi đến chỗ quá khích, đi đến chỗ cực đoan<br />- (từ Mỹ,nghĩa Mỹ) tuột<br />=silk stockings sometimes run+ bít tất tơ đôi khi bị tuột sợi<br />- ngược nước để đẻ (cá)<br />- ứng cử<br />=to run for parliament+ ứng cử vào nghị viện<br />=to run for president+ ứng cử tổng thống<br />*  ngoại động từ<br />- chạy (một quâng đường...)<br />- chạy đua, chạy thi, cho (ngựa) chạy đua<br />=to run a horse+ cho ngựa chạy đua<br />=to run a race+ chạy đua<br />- cho chạy<br />=to run a ship to...+ cho tàu chạy tới...<br />=to run a machine+ cho máy chạy<br />=to run a car into a garage+ đánh ô tô vào nhà để xe<br />- vượt qua; chọc thủng, phá vỡ<br />=to run rapids+ vượt tác ghềnh<br />=to run to a blockade+ tránh thoát vòng vây<br />- cầu, phó mặc (may rủi...)<br />=to chance+ cầu may<br />- theo, đi theo<br />=to let things run their cours+ cứ để cho mọi việc tiến hành theo lệ thường của nó<br />=to run a scent+ theo vết (thú săn)<br />- đuổi theo, rượt theo (thú săn...)<br />=to run to earth+ đuổi (chồn...) vào tận hang<br />- cho chảy; đổ (kim loại...) vào khuôn<br />=to run the water off+ cho nước chảy đi<br />=to run metal into mould+ đổ kim loại vào khuôn<br />- chỉ huy, điều khiển, quản lý, trông nom<br />=to run a hotel+ quản lý một khách sạn<br />=to run a factory+ điều khiển một nhà máy<br />=to run the show+ điều khiển mọi việc<br />- xô vào, lao vào, đụng vào<br />=to run one''s head against the wall+ lao đầu vào tường<br />- đâm vào, chọc vào<br />=to run one''s sword through somebody; to run somebody through with one''s sword+ đâm lưỡi kiếm xuyên qua người ai<br />- luồn<br />=to run a rope through a ring+ luồn sợi dây thừng qua cái vòng<br />- đưa lướt đi<br />=to run one''s hand over something+ đưa tay lướt trên vật gì<br />=to run one''s fingers through one''s hair+ đưa ngón tay lên vuốt tóc<br />- đổ tràn trề, đổ chứa chan, đổ lai láng, chảy đầm đìa, chảy ròng ròng<br />=to run wine+ đổ rượu tràn trề<br />=to run blood+ đổ máu lênh láng<br />- cho ra đồng cỏ (vật nuôi)<br />- buôn lậu<br />=to run arms+ buôn lậu khí giới<br />- khâu lược (cái áo...)<br />- gạch, vẽ (một đường...); đặt (đường dây điện thoại...)<br />- để cho chất đống (nợ nầm...)<br />- đem (so sánh...)<br />=to paralled; to run a simile+ đem so sánh, đem đối chiếu<br />- đề cử, giới thiệu; ủng hộ (một người ra ứng cử)<br />=to run a candidate+ giới thiệu người ứng cử; ủng hộ một người ứng cử<br /><br />@run<br />- chạy</Q></N></I></F></C>'),
(99979, 'rance', '<C><F><I><N><Q>@rance<br />* danh từ<br />- đá hoa Bỉ</Q></N></I></F></C>'),
(99980, 'ranch', '<C><F><I><N><Q>@ranch /rɑ:ntʃ/<br />*  danh từ<br />- trại nuôi súc vật (ở Mỹ,  Ca-na-đa)<br />*  nội động từ<br />- quản lý trại chăn nuôi</Q></N></I></F></C>'),
(99981, 'ranched', '<C><F><I><N><Q>@ranch /rɑ:ntʃ/<br />*  danh từ<br />- trại nuôi súc vật (ở Mỹ,  Ca-na-đa)<br />*  nội động từ<br />- quản lý trại chăn nuôi</Q></N></I></F></C>'),
(99982, 'rancher', '<C><F><I><N><Q>@rancher /''rɑ:ntʃə/ (ranchman) /''rɑ:ntʃə/<br />*  danh từ<br />- chủ trại nuôi súc vật<br />- người làm ở trại nuôi súc vật</Q></N></I></F></C>'),
(99983, 'ranchers', '<C><F><I><N><Q>@rancher /''rɑ:ntʃə/ (ranchman) /''rɑ:ntʃə/<br />*  danh từ<br />- chủ trại nuôi súc vật<br />- người làm ở trại nuôi súc vật</Q></N></I></F></C>'),
(99984, 'ranches', '<C><F><I><N><Q>@ranch /rɑ:ntʃ/<br />*  danh từ<br />- trại nuôi súc vật (ở Mỹ,  Ca-na-đa)<br />*  nội động từ<br />- quản lý trại chăn nuôi</Q></N></I></F></C>'),
(99985, 'ranching', '<C><F><I><N><Q>@ranch /rɑ:ntʃ/<br />*  danh từ<br />- trại nuôi súc vật (ở Mỹ,  Ca-na-đa)<br />*  nội động từ<br />- quản lý trại chăn nuôi</Q></N></I></F></C>'),
(99986, 'ranchman', '<C><F><I><N><Q>@ranchman /''rɑ:ntʃə/ (ranchman) /''rɑ:ntʃə/<br />*  danh từ<br />- chủ trại nuôi súc vật<br />- người làm ở trại nuôi súc vật</Q></N></I></F></C>'),
(99987, 'ranchmen', '<C><F><I><N><Q>@ranchman /''rɑ:ntʃə/ (ranchman) /''rɑ:ntʃə/<br />*  danh từ<br />- chủ trại nuôi súc vật<br />- người làm ở trại nuôi súc vật</Q></N></I></F></C>'),
(99988, 'rancho', '<C><F><I><N><Q>@rancho<br />* danh từ<br />- nhà tranh của người chăn nuôi<br />- nông trường chăn nuôi gia súc</Q></N></I></F></C>'),
(99989, 'rancid', '<C><F><I><N><Q>@rancid /''rænsid/<br />*  tính từ<br />- trở mùi, ôi (mỡ, bơ...)<br />=to smell rancid+ trở mùi, ôi<br />=to grow rancid+ đã trở mùi, đã ôi</Q></N></I></F></C>'),
(99990, 'rancidities', '<C><F><I><N><Q>@rancidity /ræn''siditi/ (rancidness) /''rænsidnis/<br />*  danh từ<br />- sự trở mùi, sự ôi (mỡ, bơ...)</Q></N></I></F></C>'),
(99991, 'rancidity', '<C><F><I><N><Q>@rancidity /ræn''siditi/ (rancidness) /''rænsidnis/<br />*  danh từ<br />- sự trở mùi, sự ôi (mỡ, bơ...)</Q></N></I></F></C>'),
