INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(48351, 'flash', '<C><F><I><N><Q>@flash /flæʃ/<br />*  danh từ<br />- ánh sáng loé lên; tia<br />=a flash of lightning+ ánh chớp, tia chớp<br />=a flash of hope+ tia hy vọng<br />- (hoá học) sự bốc cháy<br />- giây lát<br />=in a flash+ trong giây lát<br />=a flash of merriment+ cuộc vui trong giây lát<br />- sự phô trương<br />=out of flash+ cốt để phô trương<br />- (điện ảnh) cảnh hồi tưởng ((cũng) flash back)<br />- (quân sự) phù hiệu (chỉ đơn vị, sư đoàn...)<br />- dòng nước nâng (xả xuống phía dưới đập để nâng mực nước)<br />- tiếng lóng kẻ cắp<br />- (từ Mỹ,nghĩa Mỹ) tin ngắn; bức điện ngắn<br />!a flash in the pan<br />- chuyện đầu voi đuôi chuột<br />- người nổi tiếng nhất thời<br />*  nội động từ<br />- loé sáng, vụt sáng, chiếu sáng<br />=lightning flashes across the sky+ chớp loé sáng trên trời<br />- chợt hiện ra, chợt nảy ra, vụt hiện lên, loé lên<br />=an idea flashed upon him+ anh ta chợt nảy ra một ý kiến<br />!it flashed upon me that<br />- tôi chợt nảy ra ý kiến là<br />- chạy vụt<br />=train flashes past+ chuyến xe lửa chạy vụt qua<br />- chảy ra thành tấm (thuỷ tinh)<br />- chảy ào, chảy tràn ra (nước)<br />*  ngoại động từ<br />- làm loé lên, làm rực lên<br />=eyes flash fire+ mắt rực lửa<br />=to flash a smile at someone+ toét miệng cười với ai<br />=to flash one''s sword+ vung gươm sáng loáng<br />- truyền đi cấp tốc, phát nhanh (tin)<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) khoe, phô, thò ra khoe<br />!to flash a roll of money<br />- thò ra khoe một tập tiền<br />- làm (thuỷ tinh) chảy thành tấm<br />- phủ một lần màu lên (kính)<br />- cho nước chảy vào đầy<br />!to flash in the pan<br />- làm chuyện đầu voi đuôi chuột<br />!to flash out (up)<br />- nổi giận đùng đùng<br />*  tính từ<br />- loè loẹt, sặc sỡ<br />- giả<br />=flash money+ tiền giả<br />- lóng<br />- ăn cắp ăn nẩy</Q></N></I></F></C>'),
(48352, 'flash-board', '<C><F><I><N><Q>@flash-board /''flæʃbɔ:d/<br />*  danh từ<br />- máng nước</Q></N></I></F></C>'),
