(74847, 'margin', '<C><F><I><N><Q>@margin /''mɑ:dʤin/<br />*  danh từ<br />- mép, bờ, lề<br />=on the margin of a lake+ trên bờ hồ<br />=in the margin of the page+ ở lề trang sách<br />- số dư, số dự trữ<br />=a margin of 600d+ một số tiền dự trữ 600 đồng<br />- giới hạn<br />- (thương nghiệp) lề (số chênh lệch giữa giá vốn và giá bán)<br />!to escape death by a narrow margin<br />- suýt chết<br /><br />@margin<br />- bờ, biên, giới hạn, cận; lượng dữ trữ<br />- m. of safety hệ số an toàn</Q></N></I></F></C>'),
(74848, 'Margin, at the', '<C><F><I><N><Q>@Margin, at the<br />- (Econ) tại biên<br />+ Trong kinh tế học, tại biên có nghĩa là tại điểm mà đơn vị sản phẩm cuối cùng được sản xuất hoặc tiêu thụ.</Q></N></I></F></C>'),
(74849, 'Margin requirement', '<C><F><I><N><Q>@Margin requirement<br />- (Econ) Yêu cầu về mức chênh lệch.<br />+ Là tỷ lệ giá trị thị trường của 1 chứng khoán mà người mua có thể vay được khi mua chứng khoán đó.</Q></N></I></F></C>'),
(74850, 'Marginal', '<C><F><I><N><Q>@Marginal<br />- (Econ) Cận biên, gia lượng.<br />+ Một đơn vị biên là đơn vị tăng thên\\\\m của một cái gì đó, chẳng hạn như với CHI PHÍ BIÊN, ĐỘ THOẢ DỤNG BIÊN..</Q></N></I></F></C>'),
(74851, 'marginal', '<C><F><I><N><Q>@marginal /''mɑ:dʤinl/<br />*  tính từ<br />- (thuộc) mép, ở mép, ở bờ, ở lề<br />=marginal notes+ những lời ghi chú ở lề<br />- sát giới hạn<br />- khó trồng trọt, trồng trọt không có lợi (đất đai)<br /><br />@marginal<br />- biên duyên</Q></N></I></F></C>'),
(74852, 'Marginal analysis', '<C><F><I><N><Q>@Marginal analysis<br />- (Econ) Phân tích cận biên.<br />+ Xem NEO - CLASSICAL ECONOMICS</Q></N></I></F></C>'),
(74853, 'Marginal cost', '<C><F><I><N><Q>@Marginal cost<br />- (Econ) Chi phí cận biên.<br />+ Là chi phí tăng thêm cho việc sản xuất thêm một đơn vị sản lượng.</Q></N></I></F></C>'),
(74854, 'Marginal cost of funds schedule', '<C><F><I><N><Q>@Marginal cost of funds schedule<br />- (Econ) Biểu đồ chi phí cận biên của vốn.<br />+ Là biểu đồ xác định chi tiết chi phí thực sự của vốn tài chính của doanh nghiệp.</Q></N></I></F></C>'),
(74855, 'Marginal cost of labor', '<C><F><I><N><Q>@Marginal cost of labor<br />- (Econ) Chi phí cận biên cho lao động.</Q></N></I></F></C>'),
(74856, 'Marginal cost pricing', '<C><F><I><N><Q>@Marginal cost pricing<br />- (Econ) Định giá theo chi phí cận biên.<br />+ Là một phương pháp định giá của các hãng tư nhân hoặc các công ty nhà nước theo đó được xác định bằng chi phí biên.</Q></N></I></F></C>'),
(74857, 'Marginal damage cost', '<C><F><I><N><Q>@Marginal damage cost<br />- (Econ) Chi phí thiệt hại cận biên.<br />+ Là chi phí tăng thêm cho một thiệt hại phát sinh, thường do ô nhiễm gây ra, từ một đơn vị tăng thêm của hoạt động gây hại.</Q></N></I></F></C>'),
(74858, 'Marginal disutility', '<C><F><I><N><Q>@Marginal disutility<br />- (Econ) Độ phi thoả dụng cận biên.<br />+ Là độ phi thoả dụng tăng thêm phát sinh từ một thay đổi nhỏ trong một biến số nào đó.</Q></N></I></F></C>'),
(74859, 'Marginal efficiency of capital', '<C><F><I><N><Q>@Marginal efficiency of capital<br />- (Econ) Hiệu suất cận biên của vốn.<br />+ Là TỶ LỆ CHIẾT KHẤU độc nhất có thể khiến cho giá trị hiện tại của lợi ích ròng dự kiến từ một tài sản vốn bằng đúng với giá cung cấp nó khi giá cung cấp tài sản đó không hề tăng.</Q></N></I></F></C>'),
(74860, 'Marginal efficiency of capital schedule', '<C><F><I><N><Q>@Marginal efficiency of capital schedule<br />- (Econ) Biểu đồ hiệu suất biên của vốn.<br />+ Là biểu đồ trình bày chi tiết mối quan hệ cân bằng dài hạn giữa số vốn cần có và lãi suất.</Q></N></I></F></C>'),
(74861, 'Marginal efficiency of investment', '<C><F><I><N><Q>@Marginal efficiency of investment<br />- (Econ) Hiệu suất cận biên của đầu tư.<br />+ Còn gọi là tỷ súât lợi tức nội hoàn. Là tỷ lệ chiết khấu làm cho giá trị hiện tại của lợi tức ròng dự kiến từ một tài sản vốn bằng đúng giá cung cấp của nó trong trường hợp mức giá này được công nhận sẽ tăng lên trong ngắn hạn.</Q></N></I></F></C>'),
(74862, 'Marginal efficiency of investment schedule', '<C><F><I><N><Q>@Marginal efficiency of investment schedule<br />- (Econ) Biểu đồ hiệu suất cận biên của đầu tư.<br />+ Là đường cầu về đầu tư. Là biểu đồ trình bày chi tiết mối quan hệ giữa hiệu suất biên của đầu tư và tỷ lệ lãi suất.</Q></N></I></F></C>'),
(74863, 'Marginal firm', '<C><F><I><N><Q>@Marginal firm<br />- (Econ) Xuất biên?</Q></N></I></F></C>'),
(74864, 'Marginal income tax rate', '<C><F><I><N><Q>@Marginal income tax rate<br />- (Econ) Mức thuế suất cận biên đánh vào thu nhập.</Q></N></I></F></C>'),
(74865, 'Marginal per capita reinvestment quotient criterio', '<C><F><I><N><Q>@Marginal per capita reinvestment quotient criterio<br />- (Econ) Tiêu chuẩn về thương số tái đầu tư cận biên theo đầu người.<br />+ Là một tiêu chuẩn về đầu tư với mục tiêu tối đa hoá thu nhập bình quân đầu người tại một thời điểm trong tương lai.</Q></N></I></F></C>'),
(74866, 'Marginal physical product', '<C><F><I><N><Q>@Marginal physical product<br />- (Econ) Sản phẩm vật chất cận biên.<br />+ Là mức tăng thêm tổng sản lượng nhờ việc sử dụng thêm một đơn vị lao động và có thể dẫn xuất từ hàm sản xuất, với điều kiện các yếu tố khác không đổi.</Q></N></I></F></C>'),
(74867, 'Marginal principle', '<C><F><I><N><Q>@Marginal principle<br />- (Econ) Nguyên lý cận biên.</Q></N></I></F></C>'),
(74868, 'Marginal product', '<C><F><I><N><Q>@Marginal product<br />- (Econ) Sản phẩm cận biên.<br />+ Là sản lượng tăng thêm do sử dụng thêm một đơn vị đầu vào.</Q></N></I></F></C>'),
(74869, 'Marginal product of labors', '<C><F><I><N><Q>@Marginal product of labors<br />- (Econ) Sản phẩm cận biên của lao động.</Q></N></I></F></C>'),
(74870, 'Marginal productivity doctrine', '<C><F><I><N><Q>@Marginal productivity doctrine<br />- (Econ) Học thuyết về năng suất cận biên.<br />+ Học thuyết này cho rằng một chủ sử dụng lao động mong muốn tối đa hoá lợi nhuận của mình sẽ chịu sự chi phối của quy luật về năng suất biên giảm dần, theo đó các đơn vị lao động lần lượt được sử dụng sẽ tạo ra các đơn vị SẢN LƯỢNG giảm dần tương ứng.</Q></N></I></F></C>'),
(74871, 'Marginal propensity to comsume (MPC)', '<C><F><I><N><Q>@Marginal propensity to comsume (MPC)<br />- (Econ) Thiên hướng tiêu dùng cận biên.<br />+ Là mức thay đổi trong tiêu dùng do tăng thêm một đơn vị thu nhập.</Q></N></I></F></C>'),
(74872, 'Marginal propensity to import', '<C><F><I><N><Q>@Marginal propensity to import<br />- (Econ) Thiên hướng nhập khẩu cận biên.<br />+ Là mức thay đổi nhập khẩu do thay đổi một đơn vị thu nhập.</Q></N></I></F></C>'),
(74873, 'Marginal propensity to save (MPS)', '<C><F><I><N><Q>@Marginal propensity to save (MPS)<br />- (Econ) Thiên hướng tiết kiệm cận biên.<br />+ Là mức thay đổi tiết kiệm do thay đổi một đơn vị thu nhập.</Q></N></I></F></C>'),
(74874, 'Marginal propensity to tax', '<C><F><I><N><Q>@Marginal propensity to tax<br />- (Econ) Thiên hướng đánh thuế cận biên.<br />+ Là mức thay đổi trong thu nhập về thuế do thay đổi một đơn vị thu nhập.</Q></N></I></F></C>'),
(74875, 'Marginal propensity to withdraw', '<C><F><I><N><Q>@Marginal propensity to withdraw<br />- (Econ) Thiên hướng rút tiền cận biên.<br />+ Là mức thay đổi những khoản rút tiền do thay đổi một đơn vị thu nhập.</Q></N></I></F></C>'),
(74876, 'Marginal rate of substitution (MRS)', '<C><F><I><N><Q>@Marginal rate of substitution (MRS)<br />- (Econ) Tỷ lệ thay thế cận biên.<br />+ Trong học thuyết về cầu của người tiêu dùng, tỷ lệ thay thế biên đề cập đến số lượng của một loại hàng hoá, để bù đắp cho người tiêu dùng đối với việc từ bỏ số lượng một loại hàng hoá khác sao cho vẫn có được mức phúc lợi (thoả dụng ) như trước.</Q></N></I></F></C>'),
(74877, 'Marginal rate of tax', '<C><F><I><N><Q>@Marginal rate of tax<br />- (Econ) Thuế suất cận biên.<br />+ Là mức thuế đối với một đơn vị thu nhập tăng thêm, nhưng khái niện này cũng được áp dụng tương đương với việc tăng thêm của chi tiêu, của quà tặng.</Q></N></I></F></C>'),
(74878, 'Marginal rate of technical substitution', '<C><F><I><N><Q>@Marginal rate of technical substitution<br />- (Econ) Tỷ suất thay thế kỹ thuật cận biên; thế suất kỹ thuật cận biên.<br />+ Là tỷ lệ thay thế kỹ thuật biên của hai yếu tố đầu vào.</Q></N></I></F></C>'),
(74879, 'Marginal rate of transformation', '<C><F><I><N><Q>@Marginal rate of transformation<br />- (Econ) Tỷ lệ chuyển đổi cận biên.<br />+ Là giá trị biểu hiện bằng số của độ dốc của đường GIỚI HẠN KHẢ NĂNG SẢN XUẤT.</Q></N></I></F></C>'),
(74880, 'Marginal revenue', '<C><F><I><N><Q>@Marginal revenue<br />- (Econ) Doanh thu cận biên.<br />+ Là mức thay đổi trong tổng doanh thu phát sinh từ việc bán thêm một đơn vị sản lượng.</Q></N></I></F></C>'),
(74881, 'Marginal revenue product', '<C><F><I><N><Q>@Marginal revenue product<br />- (Econ) Sản phẩm doanh thu cận biên.<br />+ Là sản phẩm vật chất biên nhân với doanh thu biên từ việc bán thêm một đơn vị sản phẩm nhờ việc sử dụng thêm một đơn vị đầu vào.</Q></N></I></F></C>'),
(74882, 'Marginal revenue product of labor', '<C><F><I><N><Q>@Marginal revenue product of labor<br />- (Econ) Mức doanh thu cận biên của lao động.</Q></N></I></F></C>'),
(74883, 'Marginal user cost', '<C><F><I><N><Q>@Marginal user cost<br />- (Econ) Chi phí sử dụng cận biên.<br />+ Trong kinh tế học tài nguyên, đó là lợi ích ròng (việc định giá một đơn vị tài nguyên, tức là giá của nó trừ đi chi phí khai thác) mà thế hệ tương lai không có được do thế hệ hiện tại đã sử dụng một đơn vị tài nguyên hữu hạn.</Q></N></I></F></C>'),
(74884, 'Marginal utility', '<C><F><I><N><Q>@Marginal utility<br />- (Econ) Độ thoả dụng cận biên.<br />+ Là độ thoả dụng phụ thêm có được từ việc thêm một đơn vị của bất kỳ loai hàng hoá nào.</Q></N></I></F></C>'),
(74885, 'Marginal utility of income', '<C><F><I><N><Q>@Marginal utility of income<br />- (Econ) Độ thoả dụng cận biên của thu nhập.<br />+ Xem Marginal utility of money.</Q></N></I></F></C>'),
(74886, 'Marginal utility of money', '<C><F><I><N><Q>@Marginal utility of money<br />- (Econ) Độ thoả dụng cận biên của tiền.<br />+ Là tỷ lệ gia tăng thoả dụng của một cá nhân khi ngân sách của riêng người đó (thu nhập) tăng thêm 1 đơn vị.</Q></N></I></F></C>'),
(74887, 'Marginal value product of capital', '<C><F><I><N><Q>@Marginal value product of capital<br />- (Econ) Sản phẩm giá trị biên của vốn.</Q></N></I></F></C>'),
(74888, 'Marginal value product of labor', '<C><F><I><N><Q>@Marginal value product of labor<br />- (Econ) Sản phẩm giá trị biên của lao động.</Q></N></I></F></C>'),
(74889, 'marginalia', '<C><F><I><N><Q>@marginalia /,mɑ:dʤi''neiljə/<br />*  danh từ số nhiều<br />- những lời ghi chú ở lề</Q></N></I></F></C>'),
(74890, 'marginalise', '<C><F><I><N><Q>@marginalise<br />* động từ<br />- như marginalize</Q></N></I></F></C>'),
(74891, 'marginalize', '<C><F><I><N><Q>@marginalize<br />* ngoại động từ<br />- cách ly ra khỏi nhịp điệu phát triển xã hội<br />- cho là thứ yếu/không quan trọng</Q></N></I></F></C>'),
(74892, 'marginate', '<C><F><I><N><Q>@marginate /''mɑ:dʤinit/<br />*  tính từ<br />- có mép, có bờ, có lề</Q></N></I></F></C>'),
(74893, 'marginated', '<C><F><I><N><Q>@marginated<br />* tính từ<br />- như marginate</Q></N></I></F></C>'),
(74894, 'margined', '<C><F><I><N><Q>@margin /''mɑ:dʤin/<br />*  danh từ<br />- mép, bờ, lề<br />=on the margin of a lake+ trên bờ hồ<br />=in the margin of the page+ ở lề trang sách<br />- số dư, số dự trữ<br />=a margin of 600d+ một số tiền dự trữ 600 đồng<br />- giới hạn<br />- (thương nghiệp) lề (số chênh lệch giữa giá vốn và giá bán)<br />!to escape death by a narrow margin<br />- suýt chết<br /><br />@margin<br />- bờ, biên, giới hạn, cận; lượng dữ trữ<br />- m. of safety hệ số an toàn</Q></N></I></F></C>'),
(74895, 'margining', '<C><F><I><N><Q>@margin /''mɑ:dʤin/<br />*  danh từ<br />- mép, bờ, lề<br />=on the margin of a lake+ trên bờ hồ<br />=in the margin of the page+ ở lề trang sách<br />- số dư, số dự trữ<br />=a margin of 600d+ một số tiền dự trữ 600 đồng<br />- giới hạn<br />- (thương nghiệp) lề (số chênh lệch giữa giá vốn và giá bán)<br />!to escape death by a narrow margin<br />- suýt chết<br /><br />@margin<br />- bờ, biên, giới hạn, cận; lượng dữ trữ<br />- m. of safety hệ số an toàn</Q></N></I></F></C>'),
(74896, 'margins', '<C><F><I><N><Q>@margin /''mɑ:dʤin/<br />*  danh từ<br />- mép, bờ, lề<br />=on the margin of a lake+ trên bờ hồ<br />=in the margin of the page+ ở lề trang sách<br />- số dư, số dự trữ<br />=a margin of 600d+ một số tiền dự trữ 600 đồng<br />- giới hạn<br />- (thương nghiệp) lề (số chênh lệch giữa giá vốn và giá bán)<br />!to escape death by a narrow margin<br />- suýt chết<br /><br />@margin<br />- bờ, biên, giới hạn, cận; lượng dữ trữ<br />- m. of safety hệ số an toàn</Q></N></I></F></C>'),
