INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(105004, 'roll', '<C><F><I><N><Q>@roll /''roulkɔ:l/<br />*  danh từ<br />- cuốn, cuộn, súc, ổ<br />=rolls of paper+ những cuộn giấy<br />=a roll of bread+ ổ bánh mì<br />=a roll of hair+ búi tóc<br />=a roll of tobacco+ cuộn thuốc lá<br />- ổ bánh mì nhỏ (để ăn sáng...)<br />- văn kiện, hồ sơ<br />=the Rolls+ sở lưu trữ hồ sơ<br />- danh sách<br />=a roll of honour+ danh sách những người hy sinh vì tổ quốc; danh sách những người được khen tặng<br />=to call the roll+ gọi tên, điểm danh<br />- mép gập xuống (của cái gì)<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) tiền, tập tiền<br />- (kỹ thuật) trục, trục cán, tang; xylanh, con lăn, trục lăn<br />*  danh từ<br />- sự lăn tròn<br />=to have a roll on the grass+ lăn mình trên cỏ<br />- sự lắc lư, sự tròng trành (tàu biển); dáng đi lắc lư<br />- sóng cuồn cuộn<br />=the roll of the sea+ sóng biển cuồn cuộn<br />- tiếng sấm vang rền, hồi trống vang rền; lời nói thao thao nhịp nhàng<br />- (hàng không) sự lộn vòng (máy bay)<br />*  ngoại động từ<br />- lăn, vần<br />=to roll a barrel+ lăn (vần) một cái thùng<br />- cuốn, quấn, cuộn<br />=to roll a cigarette+ cuốn một điếu thuốc lá<br />=to roll a blanket+ cuốn một cái chăn<br />=to roll onself in a rug+ cuộn tròn mình trong chăn<br />=to roll oneself into a ball+ cuộn tròn lại<br />- đọc rung lên, đọc sang sảng, hát ngân vang<br />=to roll out verses+ ngâm thơ sang sảng<br />=to roll one''s rs+ rung những chữ r<br />- lăn (đường...), cán (kim loại)<br />- làm cho cuồn cuộn<br />=the river rolls its waters to the sea+ con sông đưa dòng nước cuồn cuộn chảy ra biển<br />=chimney rolls up smoke+ ống khói nhả khói lên cuồn cuộn<br />*  nội động từ<br />- lăn<br />- quay quanh (hành tinh...)<br />- lăn mình<br />=to roll on the grá+ lăn mình trên cỏ<br />=to roll in money (riches)+ ngập trong của cải<br />- ((thường) + on,  by) trôi đi, trôi qua (thời gian, năm tháng...)<br />- chạy, lăn (xe cộ); đi xe (người)<br />- chảy cuồn cuộn (sông, biển); chảy (nước mắt...)<br />- tròng trành (tàu biển...); đi lắc lư (người)<br />- rền, đổ hồi (sấm, trống...)<br />- cán được<br />=this metal rolls easily+ thứ kim loại này dễ cán<br />- (hàng không) lộn vòng<br />!to roll away<br />- lăn đi, lăn ra xa<br />- tan đi (sương mù...)<br />!to roll by<br />- trôi đi, trôi qua (thời gian...)<br />!to roll in<br />- lăn vào<br />- đổ dồn tới, đến tới tấp<br />!to roll on<br />!to roll by to roll out<br />- lăn ra, lăn ra ngoài<br />- đọc sang sảng dõng dạc<br />!to roll over<br />- lăn mình, lăn tròn<br />- đánh (ai) ngã lăn ra<br />!to roll up<br />- cuộn (thuốc lá); cuộn lại<br />=to roll onself up in a blanket+ cuộn mình trong chăn<br />- bọc lại, gói lại, bao lại<br />- tích luỹ (của cải...); chồng chất (nợ nần...)<br />- (thông tục) xuất hiện, xuất hiện bất thình lình (trên sân khấu)</Q></N></I></F></C>'),
(105005, 'roll-back', '<C><F><I><N><Q>@roll-back<br />* danh từ<br />- (Mỹ) sự hạ giá</Q></N></I></F></C>'),
