(110561, 'shaggier', '<C><F><I><N><Q>@shaggy /''ʃægi/<br />*  tính từ<br />- rậm lông, bờm xờm<br />- có nhiều bụi rậm mọc ngổn ngang<br />- có cành tua tủa<br />- đầu tóc râu ria bờm xờm (người)<br />- (thực vật học) có lông tơ dài<br />!a shaggy dog story<br />- chuyện dây cà ra dây muống cố kéo dài cho buồn cười</Q></N></I></F></C>'),
(110562, 'shaggiest', '<C><F><I><N><Q>@shaggy /''ʃægi/<br />*  tính từ<br />- rậm lông, bờm xờm<br />- có nhiều bụi rậm mọc ngổn ngang<br />- có cành tua tủa<br />- đầu tóc râu ria bờm xờm (người)<br />- (thực vật học) có lông tơ dài<br />!a shaggy dog story<br />- chuyện dây cà ra dây muống cố kéo dài cho buồn cười</Q></N></I></F></C>'),
(110563, 'shaggily', '<C><F><I><N><Q>@shaggily<br />* phó từ<br />- bờm xờm, lộn xộn<br />- có nhiều bụi rậm mọc ngổn ngang<br />- có cành tua tủa<br />- đầu tóc râu ria bờm xờm (người)<br />- &lt;thực&gt; có lông tơ dài</Q></N></I></F></C>'),
