(110741, 'sharpshooter', '<C><F><I><N><Q>@sharpshooter<br />* danh từ<br />- người bắn giỏi, nhà thiện xạ</Q></N></I></F></C>'),
(110742, 'sharpshooters', '<C><F><I><N><Q>@sharpshooter<br />* danh từ<br />- người bắn giỏi, nhà thiện xạ</Q></N></I></F></C>'),
(110743, 'shaster', '<C><F><I><N><Q>@shaster<br />* danh từ<br />- kinh; sách giáo huấn</Q></N></I></F></C>'),
(110744, 'shastra', '<C><F><I><N><Q>@shastra<br />* danh từ<br />- xem shaster</Q></N></I></F></C>'),
(110745, 'shat', '<C><F><I><N><Q>@shit /ʃit/<br />*  danh từ, qủng cứt<br />- rác rưởi<br />*  nội động từ<br />- suộc khuộng đi ỉa</Q></N></I></F></C>'),
(110746, 'shatter', '<C><F><I><N><Q>@shatter /''ʃætə/<br />*  ngoại động từ<br />- làm vỡ, làm gãy<br />- làm tan vỡ, làm tiêu tan; làm đảo lộn<br />=to shatter somebody''s hope+ làm tiêu tan hy vọng của ai<br />*  nội động từ<br />- vỡ, gãy<br />- tan vỡ, tiêu tan</Q></N></I></F></C>'),
(110747, 'shatter-brain', '<C><F><I><N><Q>@shatter-brain<br />* tính từ<br />- đãng trí, mau quên</Q></N></I></F></C>'),
(110748, 'shatter-brained', '<C><F><I><N><Q>@shatter-brained<br />* tính từ<br />- đầu óc đểnh đoảng; tinh thần phân lập; vô ý vô tứ</Q></N></I></F></C>'),
(110749, 'shattered', '<C><F><I><N><Q>@shatter /''ʃætə/<br />*  ngoại động từ<br />- làm vỡ, làm gãy<br />- làm tan vỡ, làm tiêu tan; làm đảo lộn<br />=to shatter somebody''s hope+ làm tiêu tan hy vọng của ai<br />*  nội động từ<br />- vỡ, gãy<br />- tan vỡ, tiêu tan</Q></N></I></F></C>'),
(110750, 'shattering', '<C><F><I><N><Q>@shattering<br />* tính từ<br />- rất gây rối, làm choáng</Q></N></I></F></C><C><F><I><N><Q>@shatter /''ʃætə/<br />*  ngoại động từ<br />- làm vỡ, làm gãy<br />- làm tan vỡ, làm tiêu tan; làm đảo lộn<br />=to shatter somebody''s hope+ làm tiêu tan hy vọng của ai<br />*  nội động từ<br />- vỡ, gãy<br />- tan vỡ, tiêu tan</Q></N></I></F></C>'),
(110751, 'shatterproof', '<C><F><I><N><Q>@shatterproof /''ʃætəpru:f/<br />*  tính từ<br />- không vỡ được, không gãy được</Q></N></I></F></C>'),
(110752, 'shatters', '<C><F><I><N><Q>@shatters /''ʃætəz/<br />*  danh từ số nhiều<br />- những mảnh vỡ, những mảnh gãy<br />=to smash in (into) shatters+ đập tan ra từng mảnh</Q></N></I></F></C><C><F><I><N><Q>@shatter /''ʃætə/<br />*  ngoại động từ<br />- làm vỡ, làm gãy<br />- làm tan vỡ, làm tiêu tan; làm đảo lộn<br />=to shatter somebody''s hope+ làm tiêu tan hy vọng của ai<br />*  nội động từ<br />- vỡ, gãy<br />- tan vỡ, tiêu tan</Q></N></I></F></C>'),
(110753, 'shattery', '<C><F><I><N><Q>@shattery<br />* tính từ<br />- dễ vỡ</Q></N></I></F></C>'),
(110754, 'shave', '<C><F><I><N><Q>@shave /ʃeiv/<br />*  danh từ<br />- sự cạo râu, sự cạo mặt<br />=to have a shave+ cạo râu, cạo mặt<br />- dao bào (gỗ...)<br />- sự đi sát gần; sự suýt bị (tai nạn)<br />=to have a close shave of it+ suýt nữa thì nguy, tí nữa thì chết<br />- sự đánh lừa, sựa lừa bịp<br />*  ngoại động từ (shaved; shaved,  shaven)<br />- cạo (râu, mặt)<br />- bào sơ qua (gỗ); cắt sát (cỏ)<br />- đi lướt sát (không chạm)<br />=to shave another car+ lướt sát qua một chiếc xe khác<br />- hút, suýt<br />=to shave death+ hút chết<br />*  nội động từ<br />- cạo râu, cạo mặt<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) khó mặc cả, khó chơi (trong chuyện làm ăn)</Q></N></I></F></C>'),
(110755, 'shave-hook', '<C><F><I><N><Q>@shave-hook /''ʃeiv''huk/<br />*  danh từ<br />- cái cạo gỉ (kim loại trước khi hàn)</Q></N></I></F></C>'),
