(112911, 'slap', '<C><F><I><N><Q>@slap /slæp/<br />*  danh từ<br />- cái vỗ, cái đập (bằng bàn tay)<br />=a slap on the shoulder+ cái vỗ vai<br />=a slap in the face+ cái vả vào mặt, (bóng) các đập vào mặt; sự lăng mạ; điều sỉ nhục<br />*  ngoại động từ<br />- vỗ, phát, vả<br />!to slap down<br />- phê bình, quở trách<br />*  phó từ<br />- bất thình lình; trúng<br />=to hit someone slap in the eyes+ đánh trúng vào mắt ai<br />=to run slap into someone+ đâm sầm vào ai</Q></N></I></F></C>'),
(112912, 'slap-bang', '<C><F><I><N><Q>@slap-bang /slæp''bæɳ/<br />*  phó từ<br />- thình lình, đột nhiên<br />- mạnh mẽ, dữ dội; ầm ĩ<br />- (từ Mỹ,nghĩa Mỹ) ẩu, bừa; liều lĩnh</Q></N></I></F></C>'),
(112913, 'slap-happy', '<C><F><I><N><Q>@slap-happy /''slæp''hæpi/<br />*  tính từ<br />- (từ lóng) nhộn, vui tếu<br />- (từ Mỹ,nghĩa Mỹ) say đòn<br />- (từ Mỹ,nghĩa Mỹ) ngớ ngẩn, ngốc nghếch</Q></N></I></F></C>'),
(112914, 'slap-up', '<C><F><I><N><Q>@slap-up /''slæpʌp/<br />*  tính từ<br />- (từ lóng) ác, bảnh, chiến<br />=a slap-up suit+ một bộ cánh bảnh</Q></N></I></F></C>'),
(112915, 'slapdash', '<C><F><I><N><Q>@slapdash /''slæpdæʃ/<br />*  tính từ<br />- ẩu; bừa, được đâu hay đó; liều lĩnh<br />*  danh từ<br />- công việc làm ẩu; công việc làm bừa; hành động bừa</Q></N></I></F></C>'),
(112916, 'slapjack', '<C><F><I><N><Q>@slapjack /''slæpdʤæk/<br />*  danh từ<br />- (từ Mỹ,nghĩa Mỹ) bánh kẹp, bánh xèo</Q></N></I></F></C>'),
