INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(113188, 'slip', '<C><F><I><N><Q>@slip /slip/<br />*  danh từ<br />- sự trượt chân<br />=a slip on a piece of banana-peel+ trượt vỏ chuối<br />- điều lầm lỗi; sự lỡ (lời...), sự sơ suất<br />=slip of the tongue+ điều lỡ lời<br />- áo gối, áo choàng; váy trong,  coocxê; tạp dề<br />- dây xích chó<br />- bến tàu; chỗ đóng tàu, chỗ sửa chữa tàu<br />- miếng giấy nhỏ, mảnh gỗ nhỏ, thanh gỗ dẹt<br />- cành ghép, mầm ghép; cành giâm<br />- nước áo (đất sét lỏng để tráng ngoài đồ sứ, đồ đất trước khi nung)<br />- (số nhiều) buồng sau sân khấu<br />- (số nhiều) quần xi líp<br />- cá bơn con<br />- bản in thử<br />!there''s many a slip ''twixt the cup and the lip<br />- (xem) cup<br />!to give someone the slip<br />- trốn ai, lẩn trốn ai<br />!a slip of a boy<br />- một cậu bé mảnh khảnh<br />*  ngoại động từ<br />- thả<br />=to slip anchor+ thả neo<br />- đẻ non (súc vật)<br />=cow slips calf+ bò đẻ non<br />- đút nhanh, đút gọn, đút lén, giúi nhanh, nhét nhanh, nhét gọn<br />=to slip something into one''s pocket+ nhét nhanh cái gì vào túi<br />=to slip a pill into one''s mouth+ đút gọn viên thuốc vào mồm<br />- thoát, tuột ra khỏi<br />=dog slips his collar+ chó sổng xích<br />=the point has slipped my attention+ tôi không chú ý đến điểm đó<br />=your name has slipped my momery+ tôi quên tên anh rồi<br />*  nội động từ<br />- trượt, tuột<br />=blanket slips off bed+ chăn tuột xuống đất<br />- trôi qua, chạy qua<br />=opportunity slipped+ dịp tốt trôi qua<br />- lẻn, lủi, lẩn, lỏn<br />=to slip out of the room+ lẻn ra khỏi phòng<br />- lỡ lầm, mắc lỗi (vì vô ý)<br />=to slip now and then in grammar+ thỉnh thoảng mắc lỗi về ngữ pháp<br />!to slip along<br />- (từ lóng) đi nhanh, phóng vụt đi<br />!to slip aside<br />- tránh (đấu gươm)<br />!to slip away<br />- chuồn, lẩn, trốn<br />- trôi qua (thời gian)<br />=how time slips away!+ thời giờ thấm thoắt thoi đưa!<br />!to slip by<br />- trôi qua (thời gian) ((cũng) to slip away)<br />!to slip into<br />- lẻn vào<br />- (từ lóng) đấm thình thình<br />- (từ lóng) tố cáo<br />!to slip on<br />- mặc vội áo<br />!to slip off<br />- cởi vội áo, cởi tuột ra<br />!to slip out<br />- lẻn, lỏn, lẩn<br />- thoát, tuột ra khỏi<br />- kéo ra dễ dàng (ngăn kéo)<br />!to slip over<br />- nhìn qua loa, xem xét qua loa (một vấn đề...)<br />!to slip up<br />- (thông tục) lỡ lầm, mắc lỗi<br />- thất bại; gặp điều không may<br />!to slip a cog<br />- (thông tục) (như) to slip up<br />!to slip someone over on<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) lừa bịp ai<br />!to lep slip<br />- (xem) let<br />!to let slip the gogs of war<br />- (thơ ca) bắt đầu chiến tranh, gây cuộc binh đao<br /><br />@slip<br />- sự trượt; sự dời chỗ</Q></N></I></F></C>'),
(113189, 'slip-carriage', '<C><F><I><N><Q>@slip-carriage /''slip,kæridʤ/ (slip-coach) /''slipkoutʃ/<br />-coach) <br />/''slipkoutʃ/<br />*  danh từ<br />- toa xe cắt thả (để cắt lại ở ga mà đoàn tàu không dừng)</Q></N></I></F></C>'),
