(133988, 'vaporizes', '<C><F><I><N><Q>@vaporize /''veipəraiz/ (vaporise) /''veipəraiz/<br />*  ngoại động từ<br />- làm cho bốc hơi<br />- xì, bơm (nước hoa...)<br />*  nội động từ<br />- bốc hơi, lên hơi</Q></N></I></F></C>'),
(133989, 'vaporizing', '<C><F><I><N><Q>@vaporizing /''veipəraiziɳ/<br />*  danh từ ((cũng) vaporization)<br />- sự bốc hơi<br />- sự xì, sự bơm (nước hoa)<br />*  tính từ<br />- bốc hơi</Q></N></I></F></C><C><F><I><N><Q>@vaporize /''veipəraiz/ (vaporise) /''veipəraiz/<br />*  ngoại động từ<br />- làm cho bốc hơi<br />- xì, bơm (nước hoa...)<br />*  nội động từ<br />- bốc hơi, lên hơi</Q></N></I></F></C>'),
(133990, 'vaporosity', '<C><F><I><N><Q>@vaporosity<br />- xem vaporous</Q></N></I></F></C>'),
(133991, 'vaporous', '<C><F><I><N><Q>@vaporous /''veipərəs/<br />*  tính từ<br />- (thuộc) hơi nước; giống hơi nước; có tính chất của hơi nước<br />- đầy hơi nước<br />- hư ảo</Q></N></I></F></C>'),
(133992, 'vaporously', '<C><F><I><N><Q>@vaporously<br />- xem vaporous</Q></N></I></F></C>'),
(133993, 'vapors', '<C><F><I><N><Q>@vapor /''veipə/<br />*  danh từ &amp; nội động từ<br />- (từ Mỹ,nghĩa Mỹ) (như) vapour</Q></N></I></F></C>'),
(133994, 'vaporware', '<C><F><I><N><Q>@vaporware<br />- chương trình sương mù (loại chương trình tiếp thị rất khó khăn, thậm chí đang còn trong giai đoạn hoàn thiện, không ai có thể biết các vấn đề cần hoàn chỉnh của nó sẽ giải quyết được hay không)</Q></N></I></F></C>'),
(133995, 'vapour', '<C><F><I><N><Q>@vapour /''veipə/<br />*  danh từ<br />- hơi, hơi nước<br />- vật hư ảo; sự tưởng tượng hão huyền<br />- (số nhiều) (từ cổ,nghĩa cổ) sự buồn rầu, sự chán nản, sự u uất<br />*  nội động từ<br />- bốc hơi<br />- (thông tục) nói khoác lác<br />- (thông tục) nói chuyện tầm phào</Q></N></I></F></C>'),
(133996, 'vapour bath', '<C><F><I><N><Q>@vapour bath /''veipə''bɑ:θ/ (vaporarium) /,veipə''reəriəm/<br />*  danh từ<br />- sự tắm hơi<br />- buồng tắm hơi</Q></N></I></F></C>'),
(133997, 'vapour-laden', '<C><F><I><N><Q>@vapour-laden<br />* tính từ<br />- ẩm ướt (khí trời)<br />- đầy hơi nước</Q></N></I></F></C>'),
(133998, 'vapour trail', '<C><F><I><N><Q>@vapour trail /''veipə''treil/<br />*  danh từ<br />- vệt hơi (phía sau máy bay)</Q></N></I></F></C>'),
(133999, 'vapour-trail', '<C><F><I><N><Q>@vapour-trail<br />* danh từ<br />- vệt hơi (phía sau máy bay)</Q></N></I></F></C>'),
(134000, 'vapourer', '<C><F><I><N><Q>@vapourer /''veipərə/<br />*  danh từ<br />- (thông tục) người hay khoác lác<br />- người hay nói chuyện tầm phào</Q></N></I></F></C>'),
(134001, 'vapouring', '<C><F><I><N><Q>@vapouring<br />* tính từ<br />- khoa trương, bay bướm, cầu kỳ (lời văn)<br />- tầm phào, trống trỗng (lời nói)<br />- khoe khoang, khoác lác<br />* danh từ<br />- chuyện tầm phào<br />- lời khoe khoang, lời khoác lác</Q></N></I></F></C>'),
(134002, 'vapourish', '<C><F><I><N><Q>@vapourish /''veipəriʃ/ (vapoury) /''veipəri/<br />*  tính từ<br />- như hơi nước<br />- đầy hơi nước<br />- (y học) mắc chứng u uất<br />- (văn học) lờ mờ</Q></N></I></F></C>'),
(134003, 'vapoury', '<C><F><I><N><Q>@vapoury /''veipəriʃ/ (vapoury) /''veipəri/<br />*  tính từ<br />- như hơi nước<br />- đầy hơi nước<br />- (y học) mắc chứng u uất<br />- (văn học) lờ mờ</Q></N></I></F></C>'),
(134004, 'vappa', '<C><F><I><N><Q>@vappa<br />* tính từ<br />- rượu vang bị chua</Q></N></I></F></C>'),
(134005, 'vaquero', '<C><F><I><N><Q>@vaquero<br />* danh từ<br />- người chăn bò, cao bồi</Q></N></I></F></C>'),
(134006, 'vaqueros', '<C><F><I><N><Q>@vaquero<br />* danh từ<br />- người chăn bò, cao bồi</Q></N></I></F></C>'),
(134007, 'varacious', '<C><F><I><N><Q>@varacious /ve''reiʃəs/<br />*  tính từ<br />- chân thực<br />=a varacious historian+ một nhà viết sử chân thực<br />- đúng sự thực<br />=a varacious report+ một báo cáo đúng sự thực</Q></N></I></F></C>'),
(134008, 'varan', '<C><F><I><N><Q>@varan /''værən/<br />*  danh từ<br />- (động vật học) kỳ đà xám</Q></N></I></F></C>'),
(134009, 'varec', '<C><F><I><N><Q>@varec /''værek/ (varech) /''værek/<br />*  danh từ<br />- (thực vật học) tảo bẹ<br />- tro tảo bẹ (để lấy xút)</Q></N></I></F></C>'),
(134010, 'varech', '<C><F><I><N><Q>@varech /''værek/ (varech) /''værek/<br />*  danh từ<br />- (thực vật học) tảo bẹ<br />- tro tảo bẹ (để lấy xút)</Q></N></I></F></C>'),
(134011, 'vari-coloured', '<C><F><I><N><Q>@vari-coloured<br />- Cách viết khác : varicolored</Q></N></I></F></C>'),
(134012, 'variabilities', '<C><F><I><N><Q>@Variability<br />- (Econ) Độ biến thiên.</Q></N></I></F></C><C><F><I><N><Q>@variability /''veəriəblnis/ (variability) /,veəriə''biliti/<br />*  danh từ<br />- tính hay thay đổi; tính hay biến đổi; tính biến thiên<br /><br />@variability<br />- (thống kê) [tính, độ] biến đổi</Q></N></I></F></C>'),
(134013, 'Variability', '<C><F><I><N><Q>@Variability<br />- (Econ) Độ biến thiên.</Q></N></I></F></C>'),
(134014, 'variability', '<C><F><I><N><Q>@variability /''veəriəblnis/ (variability) /,veəriə''biliti/<br />*  danh từ<br />- tính hay thay đổi; tính hay biến đổi; tính biến thiên<br /><br />@variability<br />- (thống kê) [tính, độ] biến đổi</Q></N></I></F></C>'),
(134015, 'Variable', '<C><F><I><N><Q>@Variable<br />- (Econ) Biến số.</Q></N></I></F></C>'),
(134016, 'variable', '<C><F><I><N><Q>@variable /''veəriəbl/<br />*  tính từ<br />- có thể thay đổi<br />- hay thay đổi; thay đổi, biến thiên<br />=variable length+ chiều dài thay đổi<br />=variable function+ hàm số biến thiên<br />=variable flow+ dòng chảy biến thiên<br />=variable wind+ gió hay thay đổi<br />- (từ Mỹ,nghĩa Mỹ),  (địa lý,địa chất) variable zone ôn đới<br />*  danh từ<br />- (toán học) biến số<br />- (hàng hải) gió thay đổi<br />- (số nhiều) (hàng hải) vùng (biển) không có gió thường xuyên<br /><br />@variable<br />- biến (số) // biến thiên, biến đổi<br />- additional v. biến thêm <br />- aleatory v. biến ngẫu nhiên <br />- apparent v. biến biểu kiến<br />- aritificial v. biến giả tạo<br />- auxiliary v. biến bổ trợ, biến phụ<br />- bound v. biến buộc<br />- chance v. biến ngẫu nhiên<br />- complex v. biến phức<br />- concomitant v. (thống kê) biến đồng hành <br />- constrained v. (thống kê) biến buộc<br />- contibuous v. biến liên tục<br />- contragradient v. biến phản bộ <br />- controlled v. biến bị điều khiển<br />- dependent v. biến phụ thuộc<br />- direction v. biến chỉ phương<br />- dummy v. biến giả<br />- effect v. (thống kê) biến phụ thuộc<br />- esential v. biến cốt yếu<br />- Eulerian v.s các biến Ơle<br />- free v. biến tự do<br />- hypercomplex v. biến siêu phức <br />- independent v. (giải tích) biến độc lập<br />- individual v. (logic học) biến cá thể<br />- indution v. biến quy nạp<br />- input v. biến số vào<br />- latent v. biến ẩn<br />- leading v. biến số chính<br />- main v. (điều khiển học) biến số chính<br />- marker v. (thống kê) biến số lưỡng trị<br />- missing v. (điều khiển học) biến thiếu<br />- number v. (logic học) biến số<br />- numerical v. biến số<br />- object v. (logic học) biến đối tượng<br />- orientation v.s các biến định hướng<br />- predicate v. biến vị từ<br />- process v. biến điều chỉnh<br />- proposition v. biến mệnh đề<br />- random v. (thống kê) biến ngẫu nhiên<br />- real v. biến thực <br />- space v. (