(134493, 'verbigerate', '<C><F><I><N><Q>@verbigerate<br />* nội động từ<br />- lặp đi lặp lại những câu chữ vô nghĩa (một dạng của chứng mất ngôn)</Q></N></I></F></C>'),
(134494, 'verbigeration', '<C><F><I><N><Q>@verbigeration<br />* danh từ<br />- xem verbigerate</Q></N></I></F></C>'),
(134495, 'verbose', '<C><F><I><N><Q>@verbose /və:''bous/<br />*  tính từ<br />- nói dài<br />=a verbose speaker+ người nói dài<br />- dài dòng<br />=a verbose argument+ lý lẽ dài dòng</Q></N></I></F></C>'),
(134496, 'verbosely', '<C><F><I><N><Q>@verbosely<br />* phó từ<br />- dài dòng, dông dài, dùng nhiều từ không cần thiết (nói)</Q></N></I></F></C>'),
(134497, 'verboseness', '<C><F><I><N><Q>@verboseness /və:''bousnis/ (verbosity) /və:''bɔsiti/<br />*  danh từ<br />- tính nói dài<br />- tính dài dòng (báo cáo, bài văn...)</Q></N></I></F></C>'),
(134498, 'verbosity', '<C><F><I><N><Q>@verbosity /və:''bousnis/ (verbosity) /və:''bɔsiti/<br />*  danh từ<br />- tính nói dài<br />- tính dài dòng (báo cáo, bài văn...)</Q></N></I></F></C>'),
(134499, 'verbs', '<C><F><I><N><Q>@verb /və:b/<br />*  danh từ<br />- (ngôn ngữ học) động từ<br />=transitive verb+ ngoại động từ<br />=intransitive verb+ nội động từ</Q></N></I></F></C>'),
(134500, 'verd-antique', '<C><F><I><N><Q>@verd-antique /''və:dæn''tik/<br />*  danh từ<br />- đá pocfia<br />- gỉ đồng (đồ đồng cổ)</Q></N></I></F></C>'),
(134501, 'verdancy', '<C><F><I><N><Q>@verdancy /''və:dənsi/<br />*  danh từ<br />- màu lục, màu lá cây<br />- cảnh xanh rờn (của đồng ruộng)<br />- (nghĩa bóng) tính chất ngây thơ, sự thiếu kinh nghiệm<br />=the verdancy of youth+ sự thiếu kinh nghiệm của tuổi trẻ</Q></N></I></F></C>'),
(134502, 'verdant', '<C><F><I><N><Q>@verdant /''və:dənt/<br />*  tính từ<br />- xanh lá cây, xanh tươi<br />- phủ đầy cỏ xanh<br />- (nghĩa bóng) ngây thơ, thiếu kinh nghiệm</Q></N></I></F></C>'),
(134503, 'verdantly', '<C><F><I><N><Q>@verdantly<br />- xem verdant</Q></N></I></F></C>'),
(134504, 'verdict', '<C><F><I><N><Q>@verdict /''və:dikt/<br />*  danh từ<br />- (pháp lý) lời tuyên án, lời phán quyết<br />=an open verdict+ một lời tuyên án không nói rõ thủ phạm<br />=verdict of not guilty+ sự tuyên án vô tội<br />=partial verdict+ sự tuyên án có tội một phần<br />=to return a verdict+ tuyên án<br />- sự quyết định, sự nhận định; dư luận<br />=popular verdict+ sự nhận định của nhân dân, dư luận nhân dân</Q></N></I></F></C>'),
(134505, 'verdicts', '<C><F><I><N><Q>@verdict /''və:dikt/<br />*  danh từ<br />- (pháp lý) lời tuyên án, lời phán quyết<br />=an open verdict+ một lời tuyên án không nói rõ thủ phạm<br />=verdict of not guilty+ sự tuyên án vô tội<br />=partial verdict+ sự tuyên án có tội một phần<br />=to return a verdict+ tuyên án<br />- sự quyết định, sự nhận định; dư luận<br />=popular verdict+ sự nhận định của nhân dân, dư luận nhân dân</Q></N></I></F></C>'),
(134506, 'verdigris', '<C><F><I><N><Q>@verdigris /''və:digris/<br />*  danh từ<br />- xanh đồng, gỉ đồng</Q></N></I></F></C>'),
(134507, 'verdigrised', '<C><F><I><N><Q>@verdigrised /''və:digrist/<br />*  tính từ<br />- có xanh đồng; có gỉ đồng</Q></N></I></F></C>'),
(134508, 'verdure', '<C><F><I><N><Q>@verdure /''və:dʤə/<br />*  danh từ<br />- màu xanh tươi của cây cỏ; cây cỏ xanh tươi<br />- (nghĩa bóng) sự tươi tốt<br />- (nghĩa bóng) sự non trẻ; lòng hăng hái, nhiệt tình của tuổi trẻ<br />- bức thảm có nhiều hình hoa lá</Q></N></I></F></C>'),
