INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(136354, 'washes', '<C><F><I><N><Q>@wash /wɔʃ/<br />*  danh từ<br />- sự tắm rửa, sự tắm gội, sự rửa ráy<br />=to have a wash+ tắm rửa, rửa ráy<br />- sự rửa (vật gì)<br />=to give something a wash+ rửa vật gì<br />- sự giặt, sự giặt giũ; quần áo giặt; nơi giặt<br />=to send clothes to the wash+ đem giặt quần áo<br />- nước rửa, nước gội<br />- nước rửa bát, nước vo gạo<br />- nước lã, nước ốc<br />=this soupe is a mere wash+ súp nhạt như nước ốc<br />=this tea is like wash+ nước chè này đúng là như nước lã<br />- lớp tráng, lớp thiếp (trên mặt kim loại); nước vôi (quét tường)<br />- (địa lý,địa chất) phù sa, đất bồi<br />- (hội họa) lớp màu nước (trên mặt bức hoạ)<br />- (hàng hải) sóng; tiếng sóng<br />!to come out in the wash<br />- có kết quả tốt, kết thúc tốt<br />- (từ Mỹ,nghĩa Mỹ),  (từ lóng) sớm muộn rồi cũng lộ ra, sớm muộn rồi cũng ra ánh sáng; sớm muộn rồi cũng giải thích ra<br />*  ngoại động từ<br />- rửa<br />=to wash one''s hands+ rửa tay; (nghĩa bóng) phủi tay, không chịu trách nhiệm<br />- giặt<br />- chảy, chảy sát gần, vỗ vào<br />=the sea washes the base of the cliffs+ biển vỗ vào chân vách đá<br />- cuốn đi, giạt vào<br />=to be washed over ashore+ bị giạt vào bờ<br />- khoét, nạo<br />=the water had washed a channel in the sand+ nước đã khoét thành một đường mương trong cát<br />- thấm đẫm, làm ướt<br />=washed with dew+ đẫm sương<br />=washed with tears+ đầm đìa nước mắt<br />- thiếp vàng (một kim loại); quét vôi, quét sơn (tường)<br />- (hội họa) tô màu nước (lên bức hoạ)<br />- (kỹ thuật) đãi (quặng)<br />*  nội động từ<br />- rửa ráy, tắm rửa, tắm gội<br />- giặt quần áo<br />=to wash for a living+ làm nghề giặt quần áo để kiếm sống<br />- có thể giặt được (mà không hỏng...)<br />=this stuff won''t wash+ vải len này không giặt được<br />=that won''t wash!+ (nghĩa bóng) cái đó không được!<br />=that argument won''t wash+ lý lẽ ấy không vững<br />- bị nước xói lở (con đường...)<br />!to wash away<br />- rửa sạch (vết bẩn)<br />- làm lở, cuốn trôi, cuốn đi<br />=the bank is gradually washed by the current+ bờ sông bị dòng nước làm cho lở mòn dần<br />!to wash down<br />- rửa (bằng vòi nước)<br />=to wash down a car+ rửa xe ô tô<br />- nuốt trôi, chiêu<br />=he swallows a glass of water to wash his bread down+ nó uống một cốc nước để nuốt trôi miếng bánh<br />=to wash down one''s dinner with wine+ vừa ăn vừa chiêu rượu<br />!to wash off<br />- rửa sạch, giặt sạch<br />!to wash out<br />- rửa sạch, súc sạch (cái chai)<br />- pha loãng; loãng ra, phai đi, bay mất (màu sắc), bạc màu (vì giặt nhiều)<br />=dress is quite washed out+ áo bạc hết màu<br />- có thể tẩy (rửa) đi được<br />- đãi (cát lấy vàng)<br />- giũ sạch (nợ); rửa (nhục)<br />=to wash out an insult in blood+ rửa nhục bằng máu<br />=all his debts are washed out+ nó đã giũ sạch được hết các món nợ<br />=to be (look, feel) washed out+ (thông tục) mệt lử, phờ phạc<br />!to wash up<br />- rửa bát đĩa<br />- (từ Mỹ,nghĩa Mỹ) rửa mặt, tắm gội<br />- cuốn, đưa vào bờ, giạt vào bờ<br />- (từ Mỹ,nghĩa Mỹ),  (thông tục) ((thường) dạng bị động) bị loại ra, bị bỏ ra</Q></N></I></F></C>'),
(136355, 'washier', '<C><F><I><N><Q>@  washy /''wɔʃi/<br />*  tính từ<br />- loãng, nhạt, vô vị<br />=washy soup+ cháo loãng<br />=washy food+ thức ăn nhạt<br />=washy wine+ rượu nhạt<br />- nhạt, bạc thếch (màu sắc)<br />=a washy colour+ màu bạc thếch<br />- không mặn mà, nhạt nhẽo; lòng thòng<br />=a washy style+ văn lòng thòng nhạt nhẽo</Q></N></I></F></C>'),
(136356, 'washiest', '<C><F><I><N><Q>@  washy /''wɔʃi/<br />*  tính từ<br />- loãng, nhạt, vô vị<br />=washy soup+ cháo loãng<br />=washy food+ thức ăn nhạt<br />=washy wine+ rượu nhạt<br />- nhạt, bạc thếch (màu sắc)<br />=a washy colour+ màu bạc thếch<br />- không mặn mà, nhạt nhẽo; lòng thòng<br />=a washy style+ văn lòng thòng nhạt nhẽo</Q></N></I></F></C>'),
