INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(43839, 'everybody', '<C><F><I><N><Q>@everybody /''evribɔdi/ (everyone) /''evriwʌn/<br />*  danh từ<br />- mọi người, tất cả mọi người, ai ai; mỗi người<br />=everybody else+ mọi người khác; tất cả những người khác<br />=everybody knows+ mọi người đều biết, ai ai cũng biết</Q></N></I></F></C>'),
(43840, 'everyday', '<C><F><I><N><Q>@everyday /''evridei/<br />*  tính từ<br />- hằng ngày, dùng hằng ngày<br />=one''s everyday routine+ việc làm hằng ngày<br />=everyday shoes+ giày thường đi hằng ngày<br />- thường, thông thường, xảy ra hằng ngày<br />=an everyday occurence+ việc thường xảy ra, việc thường xảy ra hằng ngày<br />- tầm thường<br />=an everyday young man+ một gã thanh niên tầm thường<br />=everyday talk+ chuyện tầm phào</Q></N></I></F></C>'),
