INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(44422, 'exhaustibility', '<C><F><I><N><Q>@exhaustibility /ig,zɔ:sti''biliti/<br />*  danh từ<br />- tính có thể làm kiệt được, tính có thể làm cạn được; tính có thể dốc hết được, tính có thể kiệt quệ, tính có thể dùng hết được<br />- tính có thể bàn hết khía cạnh, tính có thể nghiên cứu hết mọi mặt</Q></N></I></F></C>'),
(44423, 'exhaustible', '<C><F><I><N><Q>@exhaustible /ig''zɔ:stəbl/<br />*  tính từ<br />- có thể làm kiệt được, có thể làm cạn; có thể dốc hết, có thể kiệt quệ, tính có thể dùng hết<br />- có thể bàn hết khía cạnh, có thể nghiên cứu hết mọi mặt<br /><br />@exhaustible<br />- vét kiệt được<br />- normal e. vét kiệt chuẩn được</Q></N></I></F></C>'),
(44424, 'exhausting', '<C><F><I><N><Q>@exhausting /ig''zɔ:stiɳ/<br />*  tính từ<br />- làm kiệt sức, làm mệt lử</Q></N></I></F></C><C><F><I><N><Q>@exhaust /ig''zɔ:st/<br />*  danh từ<br />- (kỹ thuật) sự rút khí, sự thoát khí<br />*  ngoại động từ<br />- hút, rút (khí, hơi, nước, bụi...)<br />- làm kiệt quệ, làm rỗng, làm cạn; dốc hết, dùng hết<br />=to exhaust s well+ làm cạn một cái giếng<br />=to exhaust one''s strength+ tự làm kiệt sức (ai)<br />- bàn hết khía cạnh, nghiên cứu hết mọi mặt (vấn đề)<br /><br />@exhaust<br />- vét kiệt</Q></N></I></F></C>'),
(44425, 'exhaustingly', '<C><F><I><N><Q>@exhaustingly<br />- xem exhaust</Q></N></I></F></C>'),
(44426, 'exhaustion', '<C><F><I><N><Q>@exhaustion /ig''zɔ:stʃn/<br />*  danh từ<br />- (kỹ thuật) sự hút, sự hút hết; sự làm chân không, sự rút khí<br />- sự làm kiệt; sự dốc hết, sự dùng hết<br />- tình trạng mệt lử, tình trạng kiệt sức<br />- tình trạng kiệt quệ, tình trạng bạc màu<br />- (toán học) phép vét kiệt; phép khử liên tiếp<br />- sự bàn hết khía cạnh, sự nghiên cứu hết mọi mặt (một vấn đề)<br /><br />@exhaustion<br />- sự vét kiệt</Q></N></I></F></C>'),
(44427, 'exhaustive', '<C><F><I><N><Q>@exhaustive /ig''zɔ:stiv/<br />*  tính từ<br />- hết mọi khía cạnh, hết mọi mặt, thấu đáo, toàn diện<br />=an exhaustive research+ sự nghiên cứu toàn diện<br /><br />@exhaustive<br />- vét kiệt</Q></N></I></F></C>'),
