(57120, 'handsomeness', '<C><F><I><N><Q>@handsomeness /''hænsəmnis/<br />*  danh từ<br />- vẻ đẹp, vẻ đẹp trai<br />- sự tốt đẹp; tính rộng rãi, tính hào phóng; sự hậu hĩ<br />- lượng lớn, lượng đáng kể</Q></N></I></F></C>'),
(57121, 'handsomer', '<C><F><I><N><Q>@handsome /''hænsəm/<br />*  tính từ<br />- đẹp<br />=a handsome man+ người đẹp trai<br />- tốt đẹp, rộng rãi, hào phóng, hậu hĩ<br />=a handsome treament+ cách đối xử rộng rãi, hào phóng<br />- lớn, đáng kể<br />=a handsome fortune+ tài sản lớn<br />!handsome is that handsome does<br />- đẹp nết hơn đẹp người; xét người bằng việc làm đừng xét qua lời nói</Q></N></I></F></C>'),
(57122, 'handsomest', '<C><F><I><N><Q>@handsome /''hænsəm/<br />*  tính từ<br />- đẹp<br />=a handsome man+ người đẹp trai<br />- tốt đẹp, rộng rãi, hào phóng, hậu hĩ<br />=a handsome treament+ cách đối xử rộng rãi, hào phóng<br />- lớn, đáng kể<br />=a handsome fortune+ tài sản lớn<br />!handsome is that handsome does<br />- đẹp nết hơn đẹp người; xét người bằng việc làm đừng xét qua lời nói</Q></N></I></F></C>'),
(57123, 'handspike', '<C><F><I><N><Q>@handspike /''hændspaik/<br />*  danh từ<br />- (hàng hải) cây đòn (để điều khiển súng lớn, để điều khiển trên tàu)</Q></N></I></F></C>'),
(57124, 'handspikes', '<C><F><I><N><Q>@handspike /''hændspaik/<br />*  danh từ<br />- (hàng hải) cây đòn (để điều khiển súng lớn, để điều khiển trên tàu)</Q></N></I></F></C>'),
