(62212, 'imperatival', '<C><F><I><N><Q>@imperatival /im,perə''taivəl/<br />*  tính từ<br />- (ngôn ngữ học) (thuộc) lối mệnh lệnh</Q></N></I></F></C>'),
(62213, 'imperative', '<C><F><I><N><Q>@imperative /im''perətiv/<br />*  tính từ<br />- cấp bách, khẩn thiết<br />=an imperative need+ một nhu cầu cấp bách<br />- bắt buộc, cưỡng bách, cưỡng chế<br />=imperative orders+ mệnh lệnh có tính chất bắt buộc<br />- có tính chất sai khiến, có tính chất mệnh lệnh<br />=an imperative gesture+ một cử chỉ có tính chất mệnh lệnh<br />- (ngôn ngữ học) mệnh lệnh<br />=the imperative mood+ lối mệnh lệnh<br />*  danh từ<br />- mệnh lệnh<br />- điều đòi hỏi phải chú ý, điều đòi hỏi phải hành động; sự bắt buộc<br />- nhu cầu<br />- (ngôn ngữ học) lối mệnh lệnh; động tà ở lối mệnh lệnh</Q></N></I></F></C>'),
(62214, 'imperatively', '<C><F><I><N><Q>@imperatively<br />* phó từ<br />- với vẻ sai khiến, với vẻ ra lệnh</Q></N></I></F></C>'),
(62215, 'imperativeness', '<C><F><I><N><Q>@imperativeness /im''perətivnis/<br />*  danh từ<br />- tính chất cấp bách<br />- tính chất sai khiến, tính chất mệnh lệnh</Q></N></I></F></C>'),
(62216, 'imperatives', '<C><F><I><N><Q>@imperative /im''perətiv/<br />*  tính từ<br />- cấp bách, khẩn thiết<br />=an imperative need+ một nhu cầu cấp bách<br />- bắt buộc, cưỡng bách, cưỡng chế<br />=imperative orders+ mệnh lệnh có tính chất bắt buộc<br />- có tính chất sai khiến, có tính chất mệnh lệnh<br />=an imperative gesture+ một cử chỉ có tính chất mệnh lệnh<br />- (ngôn ngữ học) mệnh lệnh<br />=the imperative mood+ lối mệnh lệnh<br />*  danh từ<br />- mệnh lệnh<br />- điều đòi hỏi phải chú ý, điều đòi hỏi phải hành động; sự bắt buộc<br />- nhu cầu<br />- (ngôn ngữ học) lối mệnh lệnh; động tà ở lối mệnh lệnh</Q></N></I></F></C>'),
(62217, 'imperator', '<C><F><I><N><Q>@imperator /,impə''rɑ:tɔ:/<br />*  danh từ,  (sử học) La-mâ<br />- Impêrato, thống soái<br />- Hoàng đế</Q></N></I></F></C>'),
