INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(25175, 'conjurer', '<C><F><I><N><Q>@conjurer /''kʌndʤərə/ (conjuror) /''kʌndʤərə/<br />*  danh từ<br />- người làm trò ảo thuật<br />- thầy phù thuỷ, pháp sư<br />- người thông minh khác thường; người rất khéo; người có tài cán<br />=he is no conjurer+ nó chẳng có tài cán gì</Q></N></I></F></C>'),
(25176, 'conjurers', '<C><F><I><N><Q>@conjurer /''kʌndʤərə/ (conjuror) /''kʌndʤərə/<br />*  danh từ<br />- người làm trò ảo thuật<br />- thầy phù thuỷ, pháp sư<br />- người thông minh khác thường; người rất khéo; người có tài cán<br />=he is no conjurer+ nó chẳng có tài cán gì</Q></N></I></F></C>'),
(25177, 'conjures', '<C><F><I><N><Q>@conjure /''kʌndʤə/<br />*  động từ<br />- làm trò ảo thuật<br />=to conjure a rabbit out of an hat+ làm trò ảo thuật cho một con thỏ chui từ cái mũ ra<br />- làm phép phù thuỷ<br />- gọi hồn, làm hiện hồn<br />=to conjure up the spirits of the dead+ gọi hồn người chết<br />- gợi lên<br />=to conjure up visions of the past+ gợi lên những hình ảnh của quá khứ<br />- trịnh trọng kêu gọi<br />!conjure man<br />- (từ Mỹ,nghĩa Mỹ) lão phù thuỷ<br />!conjure woman<br />- (từ Mỹ,nghĩa Mỹ) mụ phù thuỷ<br />!a name of conjure with<br />- người có thế lực lớn, người có nhiều ảnh hưởng</Q></N></I></F></C>'),
(25178, 'conjuring', '<C><F><I><N><Q>@conjuring /''kʌndʤəriɳ/<br />*  danh từ<br />- trò ảo thuật<br />=what clever conjuring!+ trò ảo thuật mới khéo làm sao!<br />- phép phù thuỷ</Q></N></I></F></C><C><F><I><N><Q>@conjure /''kʌndʤə/<br />*  động từ<br />- làm trò ảo thuật<br />=to conjure a rabbit out of an hat+ làm trò ảo thuật cho một con thỏ chui từ cái mũ ra<br />- làm phép phù thuỷ<br />- gọi hồn, làm hiện hồn<br />=to conjure up the spirits of the dead+ gọi hồn người chết<br />- gợi lên<br />=to conjure up visions of the past+ gợi lên những hình ảnh của quá khứ<br />- trịnh trọng kêu gọi<br />!conjure man<br />- (từ Mỹ,nghĩa Mỹ) lão phù thuỷ<br />!conjure woman<br />- (từ Mỹ,nghĩa Mỹ) mụ phù thuỷ<br />!a name of conjure with<br />- người có thế lực lớn, người có nhiều ảnh hưởng</Q></N></I></F></C>'),
