(25520, 'constipation', '<C><F><I><N><Q>@constipation /,kɔnstə:''neiʃn/<br />*  danh từ<br />- (y học) chứng táo bón</Q></N></I></F></C>'),
(25521, 'constitionalism', '<C><F><I><N><Q>@constitionalism /,kɔnsti''tju:ʃnəlizm/<br />*  danh từ<br />- người ủng hộ hiến pháp<br />- tính chất lập hiến<br />- sự hợp hiến pháp</Q></N></I></F></C>'),
(25522, 'constitionalist', '<C><F><I><N><Q>@constitionalist /,kɔnsti''tju:ʃnəlist/<br />*  danh từ<br />- người theo chủ nghĩa lập hiến<br />- người ủng hộ chủ nghĩa lập hiến<br />- người chuyên nghiên cứu hiến pháp</Q></N></I></F></C>'),
(25523, 'constitionality', '<C><F><I><N><Q>@constitionality /''kɔnsti,tju:ʃə''næliti/<br />*  danh từ<br />- tính chất lập hiến<br />- sự hợp hiến pháp</Q></N></I></F></C>'),
(25524, 'constitionalize', '<C><F><I><N><Q>@constitionalize /,kɔnsti''tju:ʃnəlaiz/<br />*  ngoại động từ<br />- làm đúng hiến pháp, làm cho hợp với hiến pháp (một đạo luật, nghị định...)<br />*  nội động từ<br />- đi dạo, đi tản bộ</Q></N></I></F></C>'),
(25525, 'constitionally', '<C><F><I><N><Q>@constitionally /,kɔnsti''tju:ʃnəli/<br />*  phó từ<br />- hợp hiến pháp<br />- do thể tạng, do thể chất</Q></N></I></F></C>'),
(25526, 'constituencies', '<C><F><I><N><Q>@constituency /kən''stitjuənsi/<br />*  danh từ<br />- các cử tri, những người đi bỏ phiếu (của một khu vực bầu cử)<br />- khu vực bầu cử<br />- (thực vật học) khách hàng</Q></N></I></F></C>'),
(25527, 'constituency', '<C><F><I><N><Q>@constituency /kən''stitjuənsi/<br />*  danh từ<br />- các cử tri, những người đi bỏ phiếu (của một khu vực bầu cử)<br />- khu vực bầu cử<br />- (thực vật học) khách hàng</Q></N></I></F></C>'),
(25528, 'constituent', '<C><F><I><N><Q>@constituent /kən''stitjuənt/<br />*  tính từ<br />- cấu tạo, hợp thành, lập thành<br />=the constituent elements of air+ những phần tử cấu tạo không khí<br />- có quyền bầu cử<br />- lập hiến<br />=constituent assembly+ hội đồng lập hiến<br />*  danh từ<br />- phần tử, yếu tố cấu tạo, thành phần<br />- cử tri, người đi bầu (của một khu vực bầu cử)<br />- người uỷ thác, người uỷ nhiệm (cho người khác bênh vực quyền lợi của mình)<br /><br />@constituent<br />- cấu thành<br />- c. of unity cấu thành của đơn vị<br />- c. of zero cấu thành không</Q></N></I></F></C>'),
(25529, 'constituently', '<C><F><I><N><Q>@constituently<br />- xem constituent</Q></N></I></F></C>'),
(25530, 'constituents', '<C><F><I><N><Q>@constituent /kən''stitjuənt/<br />*  tính từ<br />- cấu tạo, hợp thành, lập thành<br />=the constituent elements of air+ những phần tử cấu tạo không khí<br />- có quyền bầu cử<br />- lập hiến<br />=constituent assembly+ hội đồng lập hiến<br />*  danh từ<br />- phần tử, yếu tố cấu tạo, thành phần<br />- cử tri, người đi bầu (của một khu vực bầu cử)<br />- người uỷ thác, người uỷ nhiệm (cho người khác bênh vực quyền lợi của mình)<br /><br />@constituent<br />- cấu thành<br />- c. of unity cấu thành của đơn vị<br />- c. of zero cấu thành không</Q></N></I></F></C>'),
(25531, 'constitute', '<C><F><I><N><Q>@constitute /''kɔnstitju:t/<br />*  ngoại động từ<br />- cấu tạo, tạo thành<br />=to constitute someone''s happiness+ tạo hạnh phúc cho ai<br />=twelve months constitute a years+ mười hai tháng (tạo) thành một năm<br />- thiết lập, thành lập<br />=to constitute a tribunal+ thiết lập toà án<br />- chỉ đinh, uỷ nhiệm<br />=to constitute someone arbitrator+ uỷ nhiệm ai làm trọng tải<br />=they constituted him president+ ông ta được chỉ đinh làm chủ tịch<br />!to be strongly constituted<br />- khoẻ, có thể chất khoẻ</Q></N></I></F></C>'),
(25532, 'constituted', '<C><F><I><N><Q>@constitute /''kɔnstitju:t/<br />*  ngoại động từ<br />- cấu tạo, tạo thành<br />=to constitute someone''s happiness+ tạo hạnh phúc cho ai<br />=twelve months constitute a years+ mười hai tháng (tạo) thành một năm<br />- thiết lập, thành lập<br />=to constitute a tribunal+ thiết lập toà án<br />- chỉ đinh, uỷ nhiệm<br />=to constitute someone arbitrator+ uỷ nhiệm ai làm trọng tải<br />=they constituted him president+ ông ta được chỉ đinh làm chủ tịch<br />!to be strongly constituted<br />- khoẻ, có thể chất khoẻ</Q></N></I></F></C>'),
