INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(25638, 'Consumer', '<C><F><I><N><Q>@Consumer<br />- (Econ) Người tiêu dùng<br />+ Bất cứ đơn vị kinh tế nào có nhu cầu tiêu dùng hàng hoá và dịch vụ cuối cùng.</Q></N></I></F></C>'),
(25639, 'consumer', '<C><F><I><N><Q>@consumer /kən''sju:mə/<br />*  danh từ<br />- người tiêu dùng, người tiêu thụ (hàng hoá, thực phẩm...)<br />=producers and consumers+ những người sản xuất và những người tiêu thụ<br />!consumer resistance<br />- sự thờ ơ của khách hàng<br /><br />@consumer<br />- (Tech) người tiêu thụ, người tiêu dùng, người thuê bao<br /><br />@consumer<br />- người tiêu dùng</Q></N></I></F></C>'),
(25640, 'Consumer borrowing', '<C><F><I><N><Q>@Consumer borrowing<br />- (Econ) Khoản vay cho người tiêu dùng; Vay để tiêu dùng.</Q></N></I></F></C>'),
(25641, 'Consumer choice', '<C><F><I><N><Q>@Consumer choice<br />- (Econ) Lựa chọn của người tiêu dùng.</Q></N></I></F></C>'),
(25642, 'Consumer credit', '<C><F><I><N><Q>@Consumer credit<br />- (Econ) Tín dụng tiêu dùng<br />+ Một cụm thuật ngữ chung chỉ việc cho NGƯỜI TIÊU DÙNG vay để mua hàng hoá dịch vụ, nhưng thường không bao giờ gồm việc mua nhà.</Q></N></I></F></C>'),
(25643, 'Consumer demand theory', '<C><F><I><N><Q>@Consumer demand theory<br />- (Econ) Lý thuyết cầu tiêu dùng<br />+ Lĩnh vực của môn kinh tế học xác định những lý thuyết có thể kiểm nghiệm về cách người tiêu dùng phản ứng lại với những thay đổi trong các biến như giá cả, các giá khác, thay đổi về thu nhập…</Q></N></I></F></C>'),
(25644, 'Consumer durable', '<C><F><I><N><Q>@Consumer durable<br />- (Econ) Hàng tiêu dùng lâu bền<br />+ Là bất cứ hàng tiêu dùng nào có tuổi thọ dài, do đó không được tiêu dùng ngay lập tức (như thức ăn).</Q></N></I></F></C>'),
(25645, 'consumer electronics', '<C><F><I><N><Q>@consumer electronics<br />- (Tech) đồ điện tử gia dụng</Q></N></I></F></C>'),
(25646, 'Consumer equilibrium', '<C><F><I><N><Q>@Consumer equilibrium<br />- (Econ) Cân bằng tiêu dùng<br />+ Tình huống mà người tiêu dùng tối đa hoá ĐỘ THOẢ DỤNG của mình theo RÀNG BUỘC NGÂN SÁCH.</Q></N></I></F></C>'),
(25647, 'Consumer expenditure', '<C><F><I><N><Q>@Consumer expenditure<br />- (Econ) Chi tiêu của người tiêu dùng<br />+ Xem CONSUMPTION EXPENDITURE.</Q></N></I></F></C>'),
(25648, 'consumer goods', '<C><F><I><N><Q>@consumer goods /kən''sju:mə''gudz/<br />*  danh từ<br />- hàng tiêu dùng<br /><br />@consumer goods<br />- (Tech) hàng tiêu thụ, hàng tiêu dùng</Q></N></I></F></C>'),
(25649, 'Consumer goods and services', '<C><F><I><N><Q>@Consumer goods and services<br />- (Econ) Hàng và dịch vụ tiêu dùng<br />+ HÀNG HOÁ hữu hình và vô hình được tiêu dùng theo lợi ích của bản thân để thoả mãn những nhu cầu hiện tại.</Q></N></I></F></C>'),
(25650, 'Consumer price index', '<C><F><I><N><Q>@Consumer price index<br />- (Econ) Chỉ số giá tiêu dùng<br />+ Xem RETAIL PRICE INDEX</Q></N></I></F></C>'),
(25651, 'Consumer''s surplus', '<C><F><I><N><Q>@Consumer''s surplus<br />- (Econ) Thặng dư của người tiêu dùng<br />+ Được dùng phổ biến để chỉ khu vực nằm dưới đường cầu MARSHALL của một cá nhân giữa hai mức giá.</Q></N></I></F></C>'),
(25652, 'Consumer sovereignty', '<C><F><I><N><Q>@Consumer sovereignty<br />- (Econ) Quyền tối thượng của người tiêu dùng; Chủ quyền của người tiêu dùng<br />+ Ý tưởng cho rằng NGƯỜI TIÊU DÙNG là quan toà tốt nhất đối với bản thân họ.<br /><br />@Consumer sovereignty<br />- (Econ) Chủ quyền người tiêu dùng.</Q></N></I></F></C>'),
(25653, 'Consumer surplus', '<C><F><I><N><Q>@Consumer surplus<br />- (Econ) Thặng dư người tiêu dùng.</Q></N></I></F></C>'),
(25654, 'consumerism', '<C><F><I><N><Q>@consumerism<br />* danh từ<br />- sự bảo vệ quyền lợi người tiêu dùng</Q></N></I></F></C>'),
