INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(33044, 'designed', '<C><F><I><N><Q>@design /di''zain/<br />*  danh từ<br />- đề cương, bản phác thảo, phác hoạ, đồ án<br />=the design of a machine+ bản đồ án thiết kế một cái máy<br />=the design of a book+ đề cương một quyển sách<br />- ý muốn, ý định, dự định, dự kiến, mục đích; ý đồ, mưu đồ<br />=by (with) design+ với ý định; với mục đích; cố ý để<br />=whether by accident or design+ vô tình hay hữu ý<br />=with a design to+ nhằm mục đích để<br />=to have designs on (against) somebody+ có mưu đồ ám hại ai<br />- kế hoạch<br />- cách sắp xếp, cách trình bày, cách trang trí<br />=a beautiful design of flowers+ cách cắm hoa đẹp mắt<br />- kiểu, mẫu, loại, dạng<br />=cars of laters design+ xe ô tô kiểu mới nhất<br />- khả năng sáng tạo (kiểu, cách trình bày...), tài nghĩ ra (mưu kế...); sự sáng tạo<br />*  ngoại động từ<br />- phác hoạ, vẽ phác (tranh...), vẽ kiểu (quần áo, xe ô tô...), thiết kế, làm đồ án (nhà cửa), làm đề cương, phác thảo cách trình bày (sách, tranh ảnh nghệ thuật)<br />- có ý định, định, dự kiến, trù tính; có y đồ, có mưu đồ<br />=to design to do (doing) something+ dự định làm việc gì<br />=we did not design this result+ chúng tôi không dự kiến kết quả thế này<br />- chỉ định, để cho, dành cho<br />=this little sword was not designed for real fighting+ thanh gươm nhỏ bé này không phải là để chiến đấu thực sự<br />*  nội động từ<br />- làm nghề vẽ kiểu, làm nghề xây dựng đồ án<br /><br />@design<br />- (Tech) thiết kế (d); đề án, đồ án; thiết kế (đ)<br /><br />@design<br />- thiết kế; dự án '),
(33045, 'designedly', '<C><F><I><N><Q>@designedly /di''zainidli/<br />*  phó từ<br />- có mục đích dụng ý, cố ý; có ý đồ</Q></N></I></F></C>'),
(33046, 'designer', '<C><F><I><N><Q>@designer /di''zainə/<br />*  danh từ<br />- người phác hoạ, người vẽ kiểu, người phác thảo cách trình bày (một quyển sách...), người trang trí (sân khấu...), người thiết kế (vườn, công viên...)</Q></N></I></F></C>'),
(33047, 'designers', '<C><F><I><N><Q>@designer /di''zainə/<br />*  danh từ<br />- người phác hoạ, người vẽ kiểu, người phác thảo cách trình bày (một quyển sách...), người trang trí (sân khấu...), người thiết kế (vườn, công viên...)</Q></N></I></F></C>'),
