(36128, 'distributed feedback (DFB) laser', '<C><F><I><N><Q>@distributed feedback (DFB) laser<br />- (Tech) bộ kích quang hồi tiếp phân bố</Q></N></I></F></C>'),
(36129, 'distributed information system', '<C><F><I><N><Q>@distributed information system<br />- (Tech) hệ thống thông tin phân bố</Q></N></I></F></C>'),
(36130, 'Distributed lags', '<C><F><I><N><Q>@Distributed lags<br />- (Econ) Độ trễ có phân phối.<br />+ Sự xác định về các mối quan hệ kinh tế lượng thường đòi hỏi rằng một biến giải thích không chỉ tồn tại dưới giá trị hiện tại mà còn dưới dạng một chuỗi trước đó (trễ).</Q></N></I></F></C>'),
(36131, 'Distributed profits', '<C><F><I><N><Q>@Distributed profits<br />- (Econ) Lợi nhuận được phân phối.<br />+ Phần lợi nhuận ròng do hãng phân phối dưới dạng trả lãi cổ tức cho những người sở hữu vốn cổ phiếu công ty. Xem DIVIDEND PAY.</Q></N></I></F></C>'),
(36132, 'distributes', '<C><F><I><N><Q>@distribute /dis''tribju:t/<br />*  ngoại động từ<br />- phân bổ, phân phối, phân phát<br />=to distribute letters+ phát thư<br />- rắc, rải<br />=to distribute manure over a field+ rắc phân lên khắp cánh đồng<br />- sắp xếp, xếp loại, phân loại<br />=to distribute book into classes+ sắp xếp sách thành loại<br />- (ngành in) bỏ (chữ in) (dỡ chữ xếp vào từng ô)<br /><br />@distribute<br />- phân phối, phân bố</Q></N></I></F></C>'),
(36133, 'distributing', '<C><F><I><N><Q>@distributing<br />* tính từ<br />- để phân phối</Q></N></I></F></C><C><F><I><N><Q>@distribute /dis''tribju:t/<br />*  ngoại động từ<br />- phân bổ, phân phối, phân phát<br />=to distribute letters+ phát thư<br />- rắc, rải<br />=to distribute manure over a field+ rắc phân lên khắp cánh đồng<br />- sắp xếp, xếp loại, phân loại<br />=to distribute book into classes+ sắp xếp sách thành loại<br />- (ngành in) bỏ (chữ in) (dỡ chữ xếp vào từng ô)<br /><br />@distribute<br />- phân phối, phân bố</Q></N></I></F></C>'),
(36134, 'Distribution', '<C><F><I><N><Q>@Distribution<br />- (Econ) Phân phối.</Q></N></I></F></C>'),
(36135, 'distribution', '<C><F><I><N><Q>@distribution /,distri''bju:ʃn/<br />*  danh từ<br />- sự phân bổ, sự phân phối, sự phân phát<br />- sự rắc, sự rải<br />- sự sắp xếp, sự xếp loại, sự phân loại<br />- (ngành in) bỏ chữ (xếp vào từng ô)</Q></N></I></F></C>'),
(36136, 'Distribution function', '<C><F><I><N><Q>@Distribution function<br />- (Econ) Chức năng phân phối.<br />+ Một phần của chính sách chi tiêu và thuế của chính phủ liên quan tới điều chỉnh phân phối thu nhập hoặc của cải trong xã hội.</Q></N></I></F></C>'),
(36137, 'Distribution (stats)', '<C><F><I><N><Q>@Distribution (stats)<br />- (Econ) Phân phối.</Q></N></I></F></C>'),
(36138, 'Distribution, theories of', '<C><F><I><N><Q>@Distribution, theories of<br />- (Econ) Các lý thuyết phân phối.<br />+ Các lý thuyết liên quan đến cơ chế theo đó THU NHẬP quốc dân được phân phối giữa các cá nhân và các nhóm trong nền kinh tế.</Q></N></I></F></C>'),
(36139, 'Distributional equity', '<C><F><I><N><Q>@Distributional equity<br />- (Econ) Công bằng trong phân phối.<br />+ Sự đúng mực hay công bằng theo cách mà sản phẩm của một nền kinh tế được phân phối giữa các cá nhân.</Q></N></I></F></C>'),
(36140, 'Distributional wage', '<C><F><I><N><Q>@Distributional wage<br />- (Econ) Trọng số phân phối<br />+ Một hệ số bằng số áp dụng đối với những thay đổi trong thu nhập của các cá nhân hay nhóm cá nhân và bao gồm sự BIỆN MINH PHÂN PHỐI nào đó với mục đích đánh giá hiệu quả đóng góp của một chính sách hay dự án.</Q></N></I></F></C>'),
(36141, 'distributions', '<C><F><I><N><Q>@Distribution<br />- (Econ) Phân phối.</Q></N></I></F></C><C><F><I><N><Q>@distribution /,distri''bju:ʃn/<br />*  danh từ<br />- sự phân bổ, sự phân phối, sự phân phát<br />- sự rắc, sự rải<br />- sự sắp xếp, sự xếp loại, sự phân loại<br />- (ngành in) bỏ chữ (xếp vào từng ô)</Q></N></I></F></C>'),
(36142, 'distributive', '<C><F><I><N><Q>@distributive /dis''tribjutiv/<br />*  tính từ<br />- phân bổ, phân phối, phân phát<br />- (ngôn ngữ học) phân biệt<br />=distributive pronoun+ đại từ phân biệt<br /><br />@distributive<br />- phân phối phân bố</Q></N></I></F></C>'),
(36143, 'Distributive judgement', '<C><F><I><N><Q>@Distributive judgement<br />- (Econ) Sự xem xét khía cạnh phân phối; BIỆN MINH PHÂN PHỐi.<br />+ Khi các nhà kinh tế đánh giá các chính sách hay dự án, họ gặp phải những khó khăn như các chính sách ảnh hưởng không chỉ đối với tổng sản lượng của nền kinh tế mà cả cách thức sản lượng đó và lơi ích được phân phối giữa cá cá nhân.</Q></N></I></F></C>'),
(36144, 'Distributive justice', '<C><F><I><N><Q>@Distributive justice<br />- (Econ) Công bằng về khía cạnh phân phối<br />+ Một khái niệm hay nguyên tắc đánh giá các phương án phân phối thu nhập  hoặc của cải giữa các cá nhân. Xem Distributive judgement.</Q></N></I></F></C>'),
(36145, 'distributives', '<C><F><I><N><Q>@distributive /dis''tribjutiv/<br />*  tính từ<br />- phân bổ, phân phối, phân phát<br />- (ngôn ngữ học) phân biệt<br />=distributive pronoun+ đại từ phân biệt<br /><br />@distributive<br />- phân phối phân bố</Q></N></I></F></C>'),
(36146, 'distributivity', '<C><F><I><N><Q>@distributivity<br />- tính phân phối, tính phân bố</Q></N></I></F></C>'),
(36147, 'distributor', '<C><F><I><N><Q>@distributor /dis''tribjutə/<br />*  danh từ<br />- người phân phối, người phân phát<br />- (kỹ thuật) bộ phân phối<br /><br />@distributor<br />- (máy tính) bộ phân phối, bộ phân bố<br />- pulse d. bộ phận bổ xung</Q></N></I></F></C>'),
(36148, 'distributors', '<C><F><I><N><Q>@distributor /dis''tribjutə/<br />*  danh từ<br />- người phân phối, người phân phát<br />- (kỹ thuật) bộ phân phối<br /><br />@distributor<br />- (máy tính) bộ phân phối, bộ phân bố<br />- pulse d. bộ phận bổ xung</Q></N></I></F></C>'),
(36149, 'district', '<C><F><I><N><Q>@district /''distrikt/<br />*  danh từ<br />- địa hạt, khu vực, quận, huyện, khu<br />- vùng, miền<br />=the lake district+ vùng hồ (ở miền bắc nước Anh)<br />- (từ Mỹ,nghĩa Mỹ) khu vực bầu cử<br />- (tôn giáo) giáo khu nhỏ<br />*  định ngữ<br />- thuộc địa hạt, thuộc quận, thuộc huyện, thuộc khu; thuộc giáo khu nhỏ<br />=district council+ hội đồng quận<br />=a district visistor+ bà phước của giáo khu nhỏ (chuyên đi thăm những gia đình nghèo, chăm lo việc từ thiện, cứu tế...)<br />*  ngoại động từ<br />- chia thành địa hạt, chia thành khu vực, chia thành quận, chia thành huyện, chia thành khu</Q></N></I></F></C>'),
(36150, 'district attorney', '<C><F><I><N><Q>@district attorney<br />- (Mỹ)(vt DA) biện lý quận</Q></N></I></F></C>'),
(36151, 'district-court', '<C><F><I><N><Q>@district-court<br />* danh từ<br />- toà án quận; toà án địa phương</Q></N></I></F></C>'),
(36152, 'district-heating', '<C><F><I><N><Q>@district-heating<br />* danh từ<br />- việc cung cấp nước nóng từ một nhà trung tâm cho một khu phố</Q></N></I></F></C>'),
