INSERT INTO `tbl_edict` (`id`, `word`, `detail`) VALUES
(38950, 'Econometrics', '<C><F><I><N><Q>@Econometrics<br />- (Econ) Môn kinh tế lượng.<br />+ Một nghành của thống kê học liên quan tới kiểm nghiệm các giả thuyết kinh tế và ước tính các tham số kinh tế chủ yế thông qua phương pháp HỒI QUY BỘI SỐ, mặc dù đôi khi thông qua việc sử dụng phương pháp luận phức tạp hơn.</Q></N></I></F></C>'),
(38951, 'econometrics', '<C><F><I><N><Q>@econometrics<br />* danh từ<br />- toán kinh tế<br /><br />@econometrics<br />- (thống kê) kinh tế lượng học</Q></N></I></F></C>'),
(38952, 'econometrist', '<C><F><I><N><Q>@econometrist<br />* danh từ<br />- nhà toán kinh tế</Q></N></I></F></C>'),
(38953, 'economic', '<C><F><I><N><Q>@economic /,i:kə''nɔmik/<br />*  tính từ<br />- kinh tế<br />- mang lợi, có lợi (việc kinh doanh...)<br /><br />@economic<br />- (thuộc) kinh tế</Q></N></I></F></C>'),
(38954, 'Economic base', '<C><F><I><N><Q>@Economic base<br />- (Econ) Cơ sở kinh tế.<br />+ Những hoạt đông kinh tế mà sự tăng trưởng và phát triển của chúng được coi là có vai trò quyết địng đối với  tăng trưởng kinh tế của một vùng hay của một thị trấn.</Q></N></I></F></C>'),
(38955, 'Economic base multiplier', '<C><F><I><N><Q>@Economic base multiplier<br />- (Econ) Nhân tử cơ sở kinh tế.<br />+ Một dạng của NHÂN TỬ KHU VỰC ước tính ảnh hưởng của những thay đổi trong một CƠ SỞ KINH TẾ vùng đối với toàn bộ nền kinh tế của vùng.</Q></N></I></F></C>'),
(38956, 'Economic Co-operation Administration', '<C><F><I><N><Q>@Economic Co-operation Administration<br />- (Econ) Cơ quan Hợp tác kinh tế.<br />+ Một cơ quan viện trợ kinh tế, thành lập năm 1948 do Đạo luật Trợ giúp nước ngoài của Mỹ, quản lý KẾ HOẠCH MARSHALL đối với việc phục hồi kinh tế Châu Âu sau chiến tranh thế giới thứ hai.</Q></N></I></F></C>'),
(38957, 'Economic community', '<C><F><I><N><Q>@Economic community<br />- (Econ) Cộng đồng kinh tế.<br />+ Một liên minh kinh tế giữa các nước có biểu thuế quan và chính sách thương mại chung đã dỡ bỏ các hạn chế đối với thương mại giữa các nước thành viên.</Q></N></I></F></C>'),
(38958, 'Economic cost', '<C><F><I><N><Q>@Economic cost<br />- (Econ) Chi phí kinh tế.</Q></N></I></F></C>'),
(38959, 'Economic development', '<C><F><I><N><Q>@Economic development<br />- (Econ) Phát triển kinh tế.<br />+ Quá trình cải thiện mức sônngs và sự sung túc của dân chúng của các nước đang phát triển bằng cách tăng thu nhập trên đầu người.</Q></N></I></F></C>'),
(38960, 'Economic development Committee', '<C><F><I><N><Q>@Economic development Committee<br />- (Econ) Uỷ ban Phát triển kinh tế.<br />+ Xem NATIONAL ECONOMIC DEVELOPMENT COUNCIL.</Q></N></I></F></C>'),
(38961, 'Economic development Institute', '<C><F><I><N><Q>@Economic development Institute<br />- (Econ) Viện Phát triển kinh tế.<br />+ Xem INTERNATIONAL BANK FOR RECONSTRUCTION AND DEVELOPMENT</Q></N></I></F></C>'),
(38962, 'Economic dynamics', '<C><F><I><N><Q>@Economic dynamics<br />- (Econ) Động học kinh tế.<br />+ Xem DYNAMIC ECONOMICS</Q></N></I></F></C>'),
(38963, 'Economic efficiency', '<C><F><I><N><Q>@Economic efficiency<br />- (Econ) Hiệu quả kinh tế.<br />+ Xem ALLCATIVE EFFICIENCY</Q></N></I></F></C>'),
(38964, 'Economic good', '<C><F><I><N><Q>@Economic good<br />- (Econ) Hàng hoá kinh tế.<br />+ Một mặt hàng khan hiếm và mặt hàng mà người ta sẽ lựa chọn nhiều hơn nếu có thể. Xem FREE GOOD.</Q></N></I></F></C>'),
(38965, 'Economic growth', '<C><F><I><N><Q>@Economic growth<br />- (Econ) Tăng trưởng kinh tế.<br />+ Thường để chỉ mức tăng sản lượng thực tế của SẢN PHẨM QUỐC DÂN RÒNG, mặc dù thước đo này sẽ nhạy cảm với cách tính sản phẩm quốc dân. Xem GROWTH THEORY</Q></N></I></F></C>'),
(38966, 'Economic imperialism', '<C><F><I><N><Q>@Economic imperialism<br />- (Econ) Đế quốc kinh tế.<br />+ Xem IMPERIALISM</Q></N></I></F></C>'),
(38967, 'Economic liberialism', '<C><F><I><N><Q>@Economic liberialism<br />- (Econ) Chủ nghĩa tự do kinh tế.<br />+ Học thuyết ủng hộ khả năng sử dụng nhiều nhất các thị trường và các tác nhân cạnh tranh để điều phối hoạt động kinh tế.</Q></N></I></F></C>'),
(38968, 'Economic man', '<C><F><I><N><Q>@Economic man<br />- (Econ) Con người kinh tế.<br />+ Tên đặt cho vật đựoc được sáng tạo trong kinh tế học, bằng cách đó, các cá nhân được giả định là cư sử như thể họ tối đa hoá độ thoả dụng, chịu chi phối bởi những ràng buộc, trong đó hiển nhiên nhất là thu nhập.</Q></N></I></F></C>'),
(38969, 'Economic planning', '<C><F><I><N><Q>@Economic planning<br />- (Econ) Hoạch định kinh tế.<br />+ Sự phối hợp một các có tổ chức các hoạt động kinh tế.</Q></N></I></F></C>'),
(38970, 'Economic policy', '<C><F><I><N><Q>@Economic policy<br />- (Econ) Chính sách kinh tế.<br />+ Sự điều hành của nhà nước đối với nền kinh tế của một quốc gia.</Q></N></I></F></C>'),
(38971, 'Economic price', '<C><F><I><N><Q>@Economic price<br />- (Econ) Giá kinh tế.</Q></N></I></F></C>'),
(38972, 'Economic profit', '<C><F><I><N><Q>@Economic profit<br />- (Econ) Lợi nhuận kinh tế.</Q></N></I></F></C>'),
(38973, 'Economic rate of return', '<C><F><I><N><Q>@Economic rate of return<br />- (Econ) Tỷ suất lợi nhuận kinh tế.</Q></N></I></F></C>'),
(38974, 'Economic rent', '<C><F><I><N><Q>@Economic rent<br />- (Econ) Tiền thuê kinh tế, tô kinh tế, Đặc lợi kinh tế.<br />+ Khoản tri trả cho một yếu tố sản xuất vượt mức cần thiết để giữ yếu tố đó ở mức sử dụng hiện tại.</Q></N></I></F></C>'),
(38975, 'Economic surplus', '<C><F><I><N><Q>@Economic surplus<br />- (Econ) Thặng dư kinh tế.<br />+ Chênh lệc giữa sản lượng của một nền kinh tế và chi phí cần thiết để sản xuất ra sản lượng đó, mà chi phí cần thiết là TIỀN CÔNG, KHẤU HAO VỐN, chi phí nguyên vật liệu.</Q></N></I></F></C>'),
(38976, 'Economic theory of polities', '<C><F><I><N><Q>@Economic theory of polities<br />- (Econ) Lý thuyết chính trị dựa trên kinh tế.<br />+ Một mô hình về hành vi chính trị giả định rằng là cử tri là những người tối đa hoá ĐỘ THOẢ DỤNG và các đảng phái chính trị là những TỔ CHỨC TỐI ĐA HOÁ PHIẾU BẦU.</Q></N></I></F></C>'),
(38977, 'Economic union', '<C><F><I><N><Q>@Economic union<br />- (Econ) Cộng đồng kinh tế.<br />+ Xem ECONOMIC COMMUNITY</Q></N></I></F></C>'),
(38978, 'Economic welfare', '<C><F><I><N><Q>@Economic welfare<br />- (Econ) Phúc lợi kinh tế.<br />+ Phần phúc lợi của con người xuất phts từ việc tiêu dùng hàng hoá và dịch vụ.</Q></N></I></F></C>'),
(38979, 'economical', '<C><F><I><N><Q>@economical /,i:kə''nɔmikəl/<br />*  tính từ<br />- tiết kiệm<br />- kinh tế<br /><br />@economical<br />- tiét kiện</Q></N></I></F></C>'),
(38980, 'economically', '<C><F><I><N><Q>@economically<br />* phó từ<br />- về phương diện kinh tế, về mặt kinh tế<br /><br />@economically<br />- về mặt kinh tế, một cách kinh tế</Q></N></I></F></C>'),
(38981, 'Economics', '<C><F><I><N><Q>@Economics<br />- (Econ) Kinh tế học.<br />+ Một ngành nghiên cứu về cách thức con người tự tổ chức để giải quyết vấn đề cơ bản về sự khan hiếm.</Q></N></I></F></C>'),
(38982, 'economics', '<C><F><I><N><Q>@economics /,i:kə''nɔmiks/<br />*  danh từ,  số nhiều dùng như số ít<br />- khoa kinh tế chính trị<br />- nền kinh tế (cho một nước)<br /><br />@economics<br />- kinh tế học, kinh tế quốc dân</Q></N></I></F></C>'),
(38983, 'economies', '<C><F><I><N><Q>@economy /i:''kɔnəmi/<br />*  danh từ<br />- sự quản lý kinh tế; nền kinh tế; tổ chức kinh tế<br />- sự tiết kiệm (thì giờ, tiền của...); phương pháp tiết kiệm<br />- cơ cấu tổ chức<br /><br />@economy<br />- nền kinh tế<br />- expanding e. nền kinh tế phát triển <br />- national e. nền kinh tế quốc dân<br />- political e. kinh tế chính trị học</Q></N></I></F></C>'),
(38984, 'Economies of learning', '<C><F><I><N><Q>@Economies of learning<br />- (Econ) Tính kinh tế nhờ học hành; Lợi ích kinh tế do học tập<br />+ Xem LEARNING</Q></N></I></F></C>'),
(38985, 'Economies of scale', '<C><F><I><N><Q>@Economies of scale<br />- (Econ) Tính kinh tế nhờ qui mô; Lợi thế kinh tế nhờ quy mô.<br />+ Giảm chi phí trung bình của một sản phẩm xét trong dài hạn nhờ tăng sản lượng.</Q></N></I></F></C>'),
(38986, 'economisation', '<C><F><I><N><Q>@economisation<br />* danh từ<br />- sự sử dụng tiết kiệm, sự sử dụng tối đa<br />- sự tiết kiệm, sự giảm chi</Q></N></I></F></C>'),
(38987, 'economise', '<C><F><I><N><Q>@economise /i:''kɔnəmaiz/ (economise) /i:''kɔnəmɑiz/<br />*  ngoại động từ<br />- tiết kiệm, sử dụng tối đa, sử dụng tốt nhất (cái gì)<br />*  nội động từ<br />- tiết kiệm; giảm chi</Q></N></I></F></C>'),
(38988, 'economist', '<C><F><I><N><Q>@economist /i:''kɔnəmist/<br />*  danh từ<br />- nhà kinh tế học<br />- người tiết kiệm<br />- người quản lý (tiền bạc...)<br />!rural economist<br />- nhà nông học</Q></N></I></F></C>'),
(38989, 'economists', '<C><F><I><N><Q>@economist /i:''kɔnəmist/<br />*  danh từ<br />- nhà kinh tế học<br />- người tiết kiệm<br />- người quản lý (tiền bạc...)<br />!rural economist<br />- nhà nông học</Q></N></I></F></C>'),
(38990, 'economization', '<C><F><I><N><Q>@economization /i:,kɔnəmai''zeiʃn/<br />*  danh từ<br />- sự sử dụng tiết kiệm, sự sử dụng tối đa<br />- sự tiết kiệm, sự giảm chi</Q></N></I></F></C>'),
